import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './BudgetPage.css';

const BudgetPage = () => {
  const { eventId } = useParams();
  const [event, setEvent] = useState(null);
  const [items, setItems] = useState([]);
  const [status, setStatus] = useState('pending');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchEventAndBudget = async () => {
      setIsLoading(true); // Start loading
      setError(''); // Clear previous errors
      setSuccess(''); // Clear previous success messages

      try {
        // Fetch Event Details
        const eventRes = await axios.get(`http://localhost:3001/api/events/booking/${eventId}`);
        setEvent(eventRes.data.event);

        // Fetch Budget Details using the NEW backend route
        try {
          // Use the eventId from useParams (which holds the booking_id)
          const budgetRes = await axios.get(`http://localhost:3001/api/budgets/by-booking/${eventId}`); // Use the new route
  
          if (budgetRes.data.budget) {
            // Budget exists...
            setItems(budgetRes.data.budget.items || []);
            setStatus(budgetRes.data.budget.status);
          } else {
            // No budget found...
            setItems([]);
            setStatus('pending');
          }
      }  catch (budgetErr) {
        console.error('Error fetching budget details:', budgetErr);
        // Check if it's an error other than "Not Found"
        if (!budgetErr.response || budgetErr.response.status !== 404) {
            setError('Failed to load budget details. Please try refreshing.');
             // Consider if you want to reset state on server errors (500)
             // If so, move the resets inside this block:
             // setItems([]);
             // setStatus('pending');
        } else {
             // Handle 404 (Not Found) - This is expected if no budget exists yet.
             // Only reset state if it was the *initial* load (isLoading is true)
            if (isLoading) {
                setItems([]);
                setStatus('pending');
            } else {
                // If a 404 happens *after* initial load (e.g., after submit/refresh),
                // it might indicate a problem, but don't just reset the state blindly.
                console.log("Budget fetch returned 404 after initial load.");
                setError("Couldn't re-fetch budget details (Not Found).");
            }
        }
    } finally {
        setIsLoading(false); // Stop loading regardless of success or error
    }

      } catch (err) {
        // Handle errors fetching the event
        console.error('Error fetching event details:', err);
        setError('Failed to load event data.');
        // Optionally clear event/items/status if event fetch fails
        setEvent(null);
        setItems([]);
        setStatus('pending');
      } finally {
        setIsLoading(false); // Stop loading regardless of success or error
      }
    };

    if (eventId) { // Only fetch if eventId is available
        fetchEventAndBudget();
    } else {
        setIsLoading(false); // Not loading if no eventId
        setError("Event ID is missing.");
    }

  }, [eventId]);
  

  

  const handleAddItem = () => {
    setItems([
      ...items,
      { category: 'Food', item_name: '', quantity: 1, unit_price: 0, total_price: 0 },
    ]);
  };

  const handleItemChange = (index, field, value) => {
    const updatedItems = [...items];
    updatedItems[index][field] = field === 'quantity' || field === 'unit_price' ? Number(value) : value;
    updatedItems[index].total_price = updatedItems[index].quantity * updatedItems[index].unit_price;
    setItems(updatedItems);
  };

  const handleRemoveItem = (index) => {
    const updatedItems = items.filter((_, idx) => idx !== index);
    setItems(updatedItems);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validation... (keep your existing validation)
    for (let item of items) {
      // Use Number() for robust checks
      const quantity = Number(item.quantity);
      const unit_price = Number(item.unit_price);
      if (!item.category || !item.item_name || isNaN(quantity) || quantity <= 0 || isNaN(unit_price) || unit_price < 0) {
        setError('Please fill out all fields correctly (quantity > 0, unit price >= 0).');
        return;
      }
    }


    // Ensure event ID exists before submitting
    if (!event?._id) {
      setError('Cannot submit budget: Event ID is missing.');
      return;
    }


    try {
      const res = await axios.post(`http://localhost:3001/api/budgets/${event._id}`, { items });
      setSuccess('Budget submitted successfully.');
      // Update state from the response for immediate UI feedback
      setItems(res.data.budget.items || []);
      setStatus(res.data.budget.status);
    } catch (err) {
      console.error('Error submitting budget:', err);
      setError(err.response?.data?.message || 'Failed to submit budget.');
      // Optionally: Do NOT change status if submission fails
    }
  };
  

  const totalBudget = items.reduce((sum, item) => sum + (Number(item.total_price) || 0), 0);

  // Use isLoading in your return statement:
  if (isLoading) {
    return <div className="budget-page">Loading budget details...</div>;
  }


  
  return (
    <div className="budget-page">
      <h2>Budget for Event: {event?.event_name || 'Event details unavailable'}</h2>
      <p>Status: <strong>{status}</strong></p>
      {error && <p className="error">{error}</p>}
      {success && <p className="success">{success}</p>}

      {status === 'pending' ? (
        <form onSubmit={handleSubmit}>
          {/* Your table and form elements */}
          <table>
             {/* Table Head */}
            <thead>
              <tr>
                <th>Category</th>
                <th>Item Name</th>
                <th>Quantity</th>
                <th>Unit Price</th>
                <th>Total Price</th>
                <th>Actions</th>
              </tr>
            </thead>
             {/* Table Body */}
            <tbody>
              {items.map((item, idx) => (
                <tr key={idx}>
                  <td>
                    <select
                      value={item.category}
                      onChange={(e) => handleItemChange(idx, 'category', e.target.value)}
                      required // Add required here too
                    >
                      <option value="" disabled>Select Category</option> {/* Add placeholder */}
                      <option value="Food">Food</option>
                      <option value="Logistic">Logistic</option>
                      <option value="Transport">Transport</option>
                      <option value="Other">Other</option>
                    </select>
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.item_name}
                      onChange={(e) => handleItemChange(idx, 'item_name', e.target.value)}
                      required
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      value={item.quantity}
                      onChange={(e) => handleItemChange(idx, 'quantity', e.target.value)}
                      min="1" // Keep min for browser validation
                      required
                      step="any" // Allow decimals if needed, otherwise remove
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      value={item.unit_price}
                      onChange={(e) => handleItemChange(idx, 'unit_price', e.target.value)}
                      min="0" // Keep min for browser validation
                      required
                       step="0.01" // Allow decimals for price
                    />
                  </td>
                  <td>{/* Ensure total_price is calculated correctly */}
                     {(Number(item.quantity || 0) * Number(item.unit_price || 0)).toFixed(2)}
                  </td>
                  <td>
                    <button type="button" onClick={() => handleRemoveItem(idx)}>
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button type="button" onClick={handleAddItem}>Add Item</button>
          <h3>Total Budget: {totalBudget.toFixed(2)}</h3>
          {/* Error/Success messages moved above the form */}
          <button type="submit">Submit Budget</button>
        </form>
      ) : (
        <div>
            <p>Budget has been submitted and is currently <strong>{status}</strong>.</p>
            <p>Editing is not allowed unless the status is reset to 'pending'.</p>
            {/* Optionally display the submitted items in read-only mode */}
            <h4>Submitted Items:</h4>
            {items.length > 0 ? (
                <table>
                    <thead>
                        <tr><th>Category</th><th>Item</th><th>Qty</th><th>Unit Price</th><th>Total</th></tr>
                    </thead>
                    <tbody>
                        {items.map((item, idx) => (
                            <tr key={`display-${idx}`}>
                                <td>{item.category}</td>
                                <td>{item.item_name}</td>
                                <td>{item.quantity}</td>
                                <td>{(Number(item.unit_price) || 0).toFixed(2)}</td>
                                <td>{(Number(item.total_price) || 0).toFixed(2)}</td>
                            </tr>
                        ))}
                    </tbody>
                    <tfoot>
                        <tr><td colSpan="4" style={{textAlign: 'right'}}><strong>Total:</strong></td><td><strong>{totalBudget.toFixed(2)}</strong></td></tr>
                    </tfoot>
                </table>
            ) : <p>No items found.</p>}
        </div>
      )}
    </div>
  );

};

export default BudgetPage;
