// frontend/src/students/UpcomingEvents.js
import React, { useState, useEffect, useContext /* or useSelector if using Redux */ } from 'react';
import axios from 'axios';
import styles from './UpcomingEvents.module.css';
import { FaCalendarAlt, FaClock, FaMapMarkerAlt, FaUniversity, FaInfoCircle, FaSearch, FaFilter, FaUserCheck, FaExclamationCircle } from 'react-icons/fa';
import { BeatLoader } from 'react-spinners';
// import { AuthContext } from '../context/AuthContext'; // Example: if using Context

function UpcomingEvents() {
// ... (useState hooks for events, filteredEvents, loading, error, searchTerm, selectedClub, allClubs remain the same)
const [events, setEvents] = useState([]);
const [filteredEvents, setFilteredEvents] = useState([]);
const [loading, setLoading] = useState(true);
const [error, setError] = useState(null);
const [searchTerm, setSearchTerm] = useState('');
const [selectedClub, setSelectedClub] = useState('');
const [allClubs, setAllClubs] = useState([]);
const [registrationStatus, setRegistrationStatus] = useState({});

// --- Get Logged-in Student Info ---
// How you get this depends on your state management solution.
// **Option 1: Using Context API (Example)**
// const { studentInfo } = useContext(AuthContext);
// const loggedInStudentUid = studentInfo?.uid;

// **Option 2: Using Local Storage (Simpler example)**
const getStudentUidFromStorage = () => {
        try {
        const storedInfo = localStorage.getItem('studentInfo');
        if (storedInfo) {
            return JSON.parse(storedInfo).uid; // Assuming you stored { uid: ..., ... }
        }
        } catch (e) {
            console.error("Error reading student info from localStorage", e);
        }
        return null; // Return null if not found or error
};
const loggedInStudentUid = getStudentUidFromStorage(); // Get UID when component renders

// --- Check Authentication on Mount (Optional but Recommended) ---
useEffect(() => {
    // You might want a general auth check when the app loads or layout mounts
    // to ensure the session is still valid and localStorage data isn't stale.
    // Example: call the '/api/students/auth/check-auth' endpoint.
    if (!loggedInStudentUid) {
            console.warn("UpcomingEvents: No logged-in student UID found. Registration might fail.");
            // Optionally, redirect to login or show a message
            // setError("You must be logged in to register for events.");
    }
}, [loggedInStudentUid]); // Re-check if UID changes (e.g., after login)

// --- Fetch Approved Events (useEffect remains the same) ---
    useEffect(() => {
        setLoading(true);
        setError(null);
        // Use the new backend route prefix
        axios.get('/api/student/events/upcoming', { withCredentials: true })
            .then(response => {
                const sortedEvents = response.data.sort((a, b) => new Date(a.event_date) - new Date(b.event_date));
                setEvents(sortedEvents);
                setFilteredEvents(sortedEvents);
                const clubs = [...new Set(sortedEvents.map(event => event.club_name))];
                setAllClubs(clubs.sort());
            })
            .catch(err => {
                console.error("Error fetching upcoming events:", err);
                if (err.response?.status === 401) { // Handle unauthorized specifically
                    setError("Please log in to view upcoming events.");
                    // Optionally trigger logout/redirect logic here
                } else {
                    setError(err.response?.data?.message || 'Failed to fetch events.');
                }
            })
            .finally(() => {
                setLoading(false);
            });
    }, []); // Fetch on mount

// --- Filter Events (useEffect remains the same) ---
    useEffect(() => {
        let currentEvents = [...events];
        if (searchTerm.trim()) {
            currentEvents = currentEvents.filter(event =>
                event.eid.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }
        if (selectedClub) {
            currentEvents = currentEvents.filter(event =>
                event.club_name === selectedClub
            );
        }
        setFilteredEvents(currentEvents);
    }, [searchTerm, selectedClub, events]);


// --- Handle Event Registration (Updated) ---
const handleRegister = (eventId, eventName) => {
    // Check if UID is available (retrieved from context/localStorage)
    if (!loggedInStudentUid) {
        alert('Could not identify student. Please ensure you are logged in.');
        // Optionally redirect to login
        return;
    }

    const confirmation = window.confirm(`Are you sure you want to register for "${eventName}"?`);
    if (confirmation) {
        setRegistrationStatus(prev => ({ ...prev, [eventId]: { loading: true } }));

        // POST request - NO LONGER need to send studentUid in the body
        // Backend gets it from the session via authStudent middleware.
        axios.post(`/api/student/events/register/${eventId}`,
            {}, // Send an empty object or null as body data
            { withCredentials: true } // Crucial for sending session cookie
        )
        .then(response => {
            console.log('Registration successful/status:', response.data);
            const message = response.data.message || 'Registration successful!';
            const isAlreadyRegistered = response.data.alreadyRegistered || false;

            setRegistrationStatus(prev => ({ ...prev, [eventId]: { success: true, message: message } }));

            // Update frontend state ONLY if not already registered according to the response
            if (!isAlreadyRegistered) {
                    setEvents(prevEvents => prevEvents.map(event => {
                    if (event._id === eventId) {
                        // Ensure reg_std exists and is an array before trying to spread/add
                        const currentRegStd = Array.isArray(event.reg_std) ? event.reg_std : [];
                        // Add UID if not already present (double check)
                        const updatedRegStd = currentRegStd.includes(loggedInStudentUid)
                            ? currentRegStd
                            : [...currentRegStd, loggedInStudentUid];
                        return { ...event, reg_std: updatedRegStd };
                    }
                    return event;
                    }));
            } else {
                // If already registered, ensure the button state reflects this without adding UID again
                // (This part might be redundant if initial check works, but good safety)
                setEvents(prevEvents => prevEvents.map(event => {
                    if (event._id === eventId && Array.isArray(event.reg_std) && !event.reg_std.includes(loggedInStudentUid)) {
                        // Add UID if somehow missing despite "alreadyRegistered" response
                        return { ...event, reg_std: [...event.reg_std, loggedInStudentUid] };
                    }
                    return event;
                }));
            }


                // Optionally, clear the status message after a few seconds
                setTimeout(() => {
                setRegistrationStatus(prev => ({...prev, [eventId]: undefined}));
                }, 5000);
        })
        .catch(err => {
            console.error("Error registering for event:", err);
            let errorMessage = 'Registration failed. Please try again.';
                if (err.response?.status === 401) {
                    errorMessage = "Authentication failed. Please log in again.";
                    // Optionally trigger logout/redirect logic here
                } else {
                    errorMessage = err.response?.data?.message || errorMessage;
                }
            setRegistrationStatus(prev => ({ ...prev, [eventId]: { error: true, message: errorMessage } }));
                // Optionally, clear the error message after a few seconds
                setTimeout(() => {
                setRegistrationStatus(prev => ({...prev, [eventId]: undefined}));
                }, 7000);
        });
    }
};

// --- Helper formatDateTime (remains the same) ---
    const formatDateTime = (dateString, timeSlots) => {
    /* ... same as before ... */
    const date = new Date(dateString);
    const formattedDate = date.toLocaleDateString('en-US', {
        year: 'numeric', month: 'long', day: 'numeric', weekday: 'long'
    });
    const formattedTime = timeSlots?.join(' / ') || 'Time TBD';
    return `${formattedDate} at ${formattedTime}`;
    };

// --- JSX Rendering (Mostly the same, update button logic) ---
return (
    <div className={styles.upcomingEventsContainer}>
        <h1><FaCalendarAlt /> Upcoming Approved Events</h1>

        {/* Search and Filter Controls (remain the same) */}
            <div className={styles.controls}>
                {/* ... search input ... */}
                {/* ... filter dropdown ... */}
            </div>


            {/* Loading State */}
            {loading && <div className={styles.centeredMessage}><BeatLoader color="#4A90E2" loading={loading} size={15} /><p>Loading events...</p></div>}

            {/* Error State */}
            {error && !loading && <div className={`${styles.centeredMessage} ${styles.errorMessage}`}><FaExclamationCircle size={24} /><p>{error}</p></div>}

            {/* No Events Found State */}
            {!loading && !error && filteredEvents.length === 0 && <div className={styles.centeredMessage}><p>{events.length === 0 ? "No upcoming events found." : "No events match your current search/filter."}</p></div>}


            {/* Events Grid */}
            {!loading && !error && filteredEvents.length > 0 && (
                <div className={styles.eventsGrid}>
                    {filteredEvents.map(event => {
                        // Check registration status using the UID retrieved earlier
                        // Ensure event.reg_std exists and is an array for .includes()
                        const isRegistered = loggedInStudentUid && Array.isArray(event.reg_std) && event.reg_std.includes(loggedInStudentUid);
                        const regInfo = registrationStatus[event._id]; // Get registration status for this event

                        return (
                            <div key={event._id} className={styles.eventCard}>
                                {/* ... Event details (h3, p tags) remain the same ... */}
                                <h3>{event.event_name}</h3>
                                <p className={styles.eventMeta}><FaUniversity /> <strong>Club:</strong> {event.club_name}</p>
                                <p className={styles.eventMeta}><FaCalendarAlt /> <strong>Date & Time:</strong> {formatDateTime(event.event_date, event.time_slots)}</p>
                                <p className={styles.eventMeta}><FaMapMarkerAlt /> <strong>Location:</strong> Room {event.room_number}</p>
                                <p className={styles.eventMeta}><FaInfoCircle /> <strong>EID:</strong> {event.eid}</p>
                                <p className={styles.eventDetails}>{event.event_details?.substring(0, 100)}{event.event_details?.length > 100 ? '...' : ''}</p>


                                {/* Registration Button Area (Updated logic) */}
                                {event.std_reg === 'Yes' && (
                                    <div className={styles.registrationArea}>
                                        <button
                                            className={`${styles.registerButton} ${isRegistered ? styles.registered : ''} ${regInfo?.loading ? styles.loading : ''}`}
                                            // Pass event._id to the handler
                                            onClick={() => !isRegistered && !regInfo?.loading && handleRegister(event._id, event.event_name)}
                                            // Disable if registered, or if loading registration, or if student not logged in
                                            disabled={isRegistered || regInfo?.loading || !loggedInStudentUid}
                                            title={!loggedInStudentUid ? "Please log in to register" : (isRegistered ? "You are already registered" : `Register for ${event.event_name}`)} // Add helpful title
                                        >
                                            {regInfo?.loading ? <BeatLoader size={8} color="#fff" /> : (isRegistered ? <> <FaUserCheck /> Registered </> : 'Register Now')}
                                        </button>
                                        {regInfo?.success && <span className={styles.regSuccess}>{regInfo.message}</span>}
                                        {regInfo?.error && <span className={styles.regError}>{regInfo.message}</span>}
                                        {!loggedInStudentUid && <span className={styles.regError}>Log in to register.</span>}
                                    </div>
                                )}
                                {event.std_reg !== 'Yes' && (
                                    <p className={styles.noRegistration}>Registration not available</p>
                                )}
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
);
}

export default UpcomingEvents;
