// frontend/src/students/StudentLogin.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StudentLogin.module.css';
// Assuming axios is installed (npm install axios) - often simpler than fetch for JSON/credentials
// import axios from 'axios';

const StudentLogin = () => {
    const [uid, setUid] = useState('');
    const [upassword, setUpassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        console.log('Attempting login for UID:', uid); // Keep logging for debug

        try {
            // --- UPDATED API ENDPOINT ---
            const apiUrl = 'http://localhost:3001/api/students/auth/login'; // Use the specific auth route

            const res = await fetch(apiUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ uid, upassword }), // Send uid and upassword
                credentials: 'include' // Important for sessions/cookies
            });

            const data = await res.json(); // Always try to parse JSON response

            if (res.ok && data.student) { // Check res.ok AND if student data exists
                alert('Login Successful');

                // --- STORE ALL NECESSARY INFO ---
                localStorage.setItem('studentId', data.student._id); // MongoDB _id (string)
                localStorage.setItem('studentName', data.student.uname); // Student Name (string)
                localStorage.setItem('studentUid', data.student.uid); // Numeric UID (will be stored as string)

                console.log('Login Success:', data.message);
                console.log('Stored studentId:', data.student._id);
                console.log('Stored studentName:', data.student.uname);
                console.log('Stored studentUid:', data.student.uid); // Log the stored UID

                // Navigate to dashboard
                console.log('Navigating to /student/dashboard');
                navigate('/student/dashboard');

            } else {
                // Handle errors reported by the server (e.g., invalid credentials)
                console.error('Login failed:', data.message || `HTTP status ${res.status}`);
                setError(data.message || 'Login failed. Please check your credentials.');
            }
        } catch (err) {
            // Handle network errors or other frontend issues during fetch
            console.error('Login fetch error:', err);
            setError('Login failed. Could not connect to server.');
        }
    };

    // --- Alternative using Axios (often cleaner) ---
    /*
    const handleAxiosSubmit = async (e) => {
        e.preventDefault();
        setError('');
        console.log('Attempting login for UID:', uid);

        try {
            const response = await axios.post(
                'http://localhost:3001/api/students/auth/login',
                { uid, upassword },
                { withCredentials: true } // Axios equivalent of credentials: 'include'
            );

            if (response.data && response.data.student) {
                 alert('Login Successful');
                 localStorage.setItem('studentId', response.data.student._id);
                 localStorage.setItem('studentName', response.data.student.uname);
                 localStorage.setItem('studentUid', response.data.student.uid);

                 console.log('Login Success:', response.data.message);
                 console.log('Stored studentId:', response.data.student._id);
                 console.log('Stored studentName:', response.data.student.uname);
                 console.log('Stored studentUid:', response.data.student.uid);

                 console.log('Navigating to /student/dashboard');
                 navigate('/student/dashboard');
            } else {
                 // Should not happen if backend sends correct response, but good practice
                 setError('Login failed: Invalid response from server.');
            }

        } catch (axiosError) {
            console.error('Login Axios error:', axiosError);
            if (axiosError.response) {
                // Error response from server (e.g., 401 Unauthorized)
                setError(axiosError.response.data.message || 'Login failed. Please check your credentials.');
            } else if (axiosError.request) {
                // Request made but no response received
                setError('Login failed: No response from server.');
            } else {
                // Other errors (e.g., setting up the request)
                setError('Login failed: An error occurred.');
            }
        }
    };
    */


    return (
        <div className={styles.studentLoginBody}>
            <div className={styles.studentLoginContainer}>
                <img src="/images/oca.jpg" alt="Student Logo" className={styles.studentLoginContainer__img} />
                <h2 className={styles.studentLoginContainer__h2}>Student Login</h2>
                {/* Use handleSubmit (fetch) or handleAxiosSubmit */}
                <form onSubmit={handleSubmit}>
                    <div className={styles.studentLoginContainer__inputGroup}>
                        <input
                            type="number" // Keep as number input
                            name="uid"
                            value={uid}
                            // Prevent non-numeric input if desired, although type="number" helps
                            onChange={(e) => setUid(e.target.value)}
                            required
                            placeholder=" "
                            className={styles.studentLoginContainer__inputGroup__input}
                        />
                        <label className={styles.studentLoginContainer__inputGroup__label}>Enter Student ID</label>
                    </div>
                    <div className={styles.studentLoginContainer__inputGroup}>
                        <input
                            type="password"
                            name="upassword"
                            value={upassword}
                            onChange={(e) => setUpassword(e.target.value)}
                            required
                            placeholder=" "
                            className={styles.studentLoginContainer__inputGroup__input}
                        />
                        <label className={styles.studentLoginContainer__inputGroup__label}>Enter your password</label>
                    </div>
                    {/* Remember me and Forgot password links */}
                    {/* ... */}
                    <button type="submit" className={styles.studentLoginContainer__button}>Log In</button>
                    {error && <p className={styles.errorMessage} style={{ color: 'red', textAlign: 'center', marginTop: '10px' }}>{error}</p>}
                    {/* Register and Back links */}
                    {/* ... */}
                </form>
            </div>
        </div>
    );
};

export default StudentLogin;
