import React, { useEffect, useState } from 'react';
import './JoinClubsPage.css';

const JoinClubsPage = ({ studentId }) => {
  const [recruitments, setRecruitments] = useState([]);
  const [search, setSearch] = useState('');
  const [filtered, setFiltered] = useState([]);
  const [pendingCount, setPendingCount] = useState(0);

  useEffect(() => {
    fetch('http://localhost:3001/api/recruitments/all')
      .then(res => res.json())
      .then(data => {
        const openRecruitments = data.filter(r => r.status === 'yes');
        setRecruitments(openRecruitments);
        setFiltered(openRecruitments);
      });

    fetch(`http://localhost:3001/api/students/${studentId}`)
      .then(res => res.json())
      .then(data => {
        setPendingCount(data.pen_clubs.length);
      });
  }, [studentId]);

  const handleSearch = () => {
    const text = search.toLowerCase();
    const results = recruitments.filter(r =>
      r.clubName.toLowerCase().includes(text) ||
      r.clubId.toLowerCase().includes(text)
    );
    setFiltered(results);
  };

  const handleRegister = async (clubId) => {
    try {
      const res = await fetch('http://localhost:3001/api/recruitments/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ studentId, clubId })
      });
      const data = await res.json();
      alert(data.message);
    } catch (err) {
      console.error('Registration error:', err);
    }
  };

  return (
    <div className="join-page">
      <h1>Join Clubs</h1>
      <p className="pending-count">Pending Interviews: {pendingCount}</p>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search by club name or ID"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div className="club-grid">
        {filtered.map(r => (
          <div key={r._id} className="club-card">
            <h3>{r.clubName}</h3>
            <p>{r.description}</p>
            <button onClick={() => handleRegister(r.clubId)}>Register</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default JoinClubsPage;
