import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { FaTrashAlt, FaFileInvoiceDollar, FaCalendarAlt, FaClock, FaMapMarkerAlt, FaUserGraduate, FaInfoCircle, FaCommentDots } from 'react-icons/fa'; // More modern icons
import './PendingEvents.css';

const PendingEvents = () => {
    const navigate = useNavigate();
    const [pendingEvents, setPendingEvents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPendingEvents = async () => {
            setLoading(true);
            setError(null);
            try {
                const clubName = localStorage.getItem('clubName');
                if (!clubName) {
                    setError('Club name not found. Please log in again.');
                    setLoading(false);
                    return;
                }

                const response = await axios.get(
                    `http://localhost:3001/api/events/club/${clubName}`,
                    { withCredentials: true }
                );

                const pending = response.data.filter(event => event.status === 'pending');
                const sortedEvents = [...pending].sort((a, b) => new Date(a.event_date) - new Date(b.event_date));
                setPendingEvents(sortedEvents);
                setLoading(false);

            } catch (err) {
                console.error('Error fetching pending events:', err);
                setError('Failed to load pending events.');
                setLoading(false);
            }
        };

        fetchPendingEvents();
    }, [navigate]);

    const handleDeleteEvent = async (eventId) => {
        if (window.confirm('Are you sure you want to delete this event?')) {
            try {
                await axios.delete(`http://localhost:3001/api/events/${eventId}`, { withCredentials: true });
                setPendingEvents(pendingEvents.filter(event => event.booking_id !== eventId));
                alert('Event deleted successfully!');
            } catch (err) {
                console.error('Error deleting event:', err);
                alert('Failed to delete event.');
            }
        }
    };

    const handleBudgetClick = (eventId) => {
        navigate(`/club/events/budget/${eventId}`);
    };

    if (loading) {
        return <div className="pending-events-container ultra-modern loading">Loading pending requests...</div>;
    }

    if (error) {
        return <div className="pending-events-container ultra-modern error">Error: {error}</div>;
    }

    return (
        <div className="pending-events-container ultra-modern">
            <h2 className="pending-events-title ultra-modern">Awaiting Your Approval</h2>
            {pendingEvents.length === 0 ? (
                <p className="no-pending-events ultra-modern">No pending event requests right now. Time to relax!</p>
            ) : (
                <div className="event-list ultra-modern">
                    {pendingEvents.map((event, index) => (
                        <div key={event.booking_id} className="event-card ultra-modern">
                            <div className="event-identity ultra-modern">
                                <span className="serial ultra-modern">{index + 1}.</span>
                                <h3 className="event-name ultra-modern">{event.event_name}</h3>
                            </div>
                            <div className="event-details ultra-modern">
                                <div className="detail-line ultra-modern">
                                    <FaCalendarAlt className="icon ultra-modern" />
                                    <span>{new Date(event.event_date).toLocaleDateString()}</span>
                                </div>
                                <div className="detail-line ultra-modern">
                                    <FaClock className="icon ultra-modern" />
                                    <span>{Array.isArray(event.time_slots) ? event.time_slots.join(', ') : event.time_slot}</span>
                                </div>
                                <div className="detail-line ultra-modern">
                                    <FaMapMarkerAlt className="icon ultra-modern" />
                                    <span>Room: {event.room_number}</span>
                                </div>
                                <div className="detail-line ultra-modern">
                                    <FaUserGraduate className="icon ultra-modern" />
                                    <span>Student Reg: {event.std_reg}</span>
                                </div>
                                <div className="detail-line expandable ultra-modern">
                                    <input type="checkbox" id={`details-${event.booking_id}`} className="details-toggle ultra-modern" />
                                    <label htmlFor={`details-${event.booking_id}`} className="details-label ultra-modern">
                                        <FaInfoCircle className="icon ultra-modern" /> More Info
                                    </label>
                                    <div className="expanded-content ultra-modern">
                                        <p>{event.event_details}</p>
                                    </div>
                                </div>
                                {event.comments && (
                                    <div className="detail-line comments ultra-modern">
                                        <FaCommentDots className="icon ultra-modern" />
                                        <span><strong>Comments:</strong> {event.comments}</span>
                                    </div>
                                )}
                            </div>
                            <div className="event-actions ultra-modern">
                                <button
                                    className="budget-button ultra-modern"
                                    onClick={() => handleBudgetClick(event.booking_id)}
                                >
                                    <FaFileInvoiceDollar className="icon ultra-modern" /> Budget
                                </button>
                                <button
                                    className="delete-button ultra-modern"
                                    onClick={() => handleDeleteEvent(event.booking_id)}
                                >
                                    <FaTrashAlt className="icon ultra-modern" /> Delete
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default PendingEvents;
