import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ApprovedEvents.css'; // Assuming you name your CSS file ApprovedEvents.css
import { FaKey, FaCalendarAlt, FaClock, FaHome, FaBuilding, FaDollarSign, FaTrashAlt } from 'react-icons/fa';

const ApprovedEvents = () => {
    const [approvedEvents, setApprovedEvents] = useState([]);
    const [searchEid, setSearchEid] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchApprovedEvents();
    }, []);

    const fetchApprovedEvents = async (eid) => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get(`/api/events/approved${eid ? `?eid=${eid}` : ''}`);
            setApprovedEvents(response.data);
            setLoading(false);
        } catch (err) {
            setError(err.message || 'Failed to fetch approved events.');
            setLoading(false);
        }
    };

    const handleSearchChange = (event) => {
        setSearchEid(event.target.value);
    };

    const handleSearchSubmit = () => {
        fetchApprovedEvents(searchEid);
    };

    const handleDeleteEvent = async (eid) => {
        if (window.confirm('Are you sure you want to delete this event?')) {
            try {
                await axios.delete(`/api/events/eid/${eid}`);
                // After successful deletion, refresh the event list
                fetchApprovedEvents(searchEid);
            } catch (err) {
                setError(err.message || 'Failed to delete event.');
            }
        }
    };

    const handleBudgetClick = (eid) => {
        // You would typically navigate to a budget page for this event's eid
        alert(`View budget for event EID: ${eid}`);
        // In a real application, you'd use a routing library (like React Router)
        // to navigate to a /events/${eid}/budget page.
    };

    if (loading) {
        return <div className="loading-modern">Loading approved events...</div>;
    }

    if (error) {
        return <div className="error-modern">Error: {error}</div>;
    }

    return (
        <div className="pending-events-container modern">
            <h1 className="pending-events-title modern">Approved Events</h1>

            <div style={{ marginBottom: '20px' }}>
                <input
                    type="text"
                    id="searchEid"
                    placeholder="Search by Event ID (EID)"
                    className="modern"
                    style={{ padding: '10px', borderRadius: '6px', border: '1px solid #ccc', width: '300px', marginRight: '10px' }}
                    value={searchEid}
                    onChange={handleSearchChange}
                />
                <button onClick={handleSearchSubmit} className="modern budget-button">Search</button>
                {searchEid && (
                    <button onClick={() => { setSearchEid(''); fetchApprovedEvents(); }} className="modern" style={{ padding: '10px', borderRadius: '6px', border: '1px solid #ccc', marginLeft: '10px' }}>Clear Search</button>
                )}
            </div>

            {approvedEvents.length === 0 ? (
                <div className="no-pending-events modern">No approved events found.</div>
            ) : (
                <ul className="pending-events-list modern">
                    {approvedEvents.map((event, index) => (
                        <li key={event._id} className="pending-event-item modern">
                            <div className="event-header modern">
                                <span className="serial-number modern">{index + 1}.</span>
                                <h2 className="event-name modern">{event.event_name}</h2>
                                <span className={`status-tag ${event.status.toLowerCase()} modern`}>{event.status}</span>
                            </div>
                            <div className="event-details-grid modern">
                                <div className="detail-item modern">
                                    <FaKey style={{ marginRight: '8px', color: '#7f8c8d' }} />
                                    EID: {event.eid}
                                </div>
                                <div className="detail-item modern">
                                    <FaCalendarAlt style={{ marginRight: '8px', color: '#7f8c8d' }} />
                                    Date: {new Date(event.event_date).toLocaleDateString()}
                                </div>
                                <div className="detail-item modern">
                                    <FaClock style={{ marginRight: '8px', color: '#7f8c8d' }} />
                                    Time: {event.time_slots.join(', ')}
                                </div>
                                <div className="detail-item modern">
                                    <FaHome style={{ marginRight: '8px', color: '#7f8c8d' }} />
                                    Room: {event.room_number}
                                </div>
                                <div className="detail-item modern">
                                    <FaBuilding style={{ marginRight: '8px', color: '#7f8c8d' }} />
                                    Club: {event.club_name}
                                </div>
                                <div className="detail-item modern">Registered By: {event.std_reg}</div>
                                {event.event_details && <div className="details-long modern">Details: {event.event_details}</div>}
                                {event.comments && <div className="comments-long modern"><strong>Comments:</strong> {event.comments}</div>}
                            </div>
                            <div className="event-actions modern">
                                <button onClick={() => handleBudgetClick(event.eid)} className="budget-button modern">
                                    <FaDollarSign style={{ marginRight: '8px' }} />
                                    Budget
                                </button>
                                <button onClick={() => handleDeleteEvent(event.eid)} className="delete-button modern">
                                    <FaTrashAlt style={{ marginRight: '8px' }} />
                                    Delete
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ApprovedEvents;
