import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StudentDashboard.module.css'; // Create this CSS file

const StudentDashboard = () => {
    const navigate = useNavigate();
    const [studentName, setStudentName] = useState('');
    const [studentId, setStudentId] = useState('');
    const [clubs, setClubs] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const storedStudentName = localStorage.getItem('studentName');
        const storedStudentId = localStorage.getItem('studentId');

        if (storedStudentName && storedStudentId) {
            setStudentName(storedStudentName);
            setStudentId(storedStudentId);
            fetchStudentClubs(storedStudentId); // Fetch clubs upon component mount
        } else {
            // If no student info in local storage, redirect to login
            navigate('/login-student');
        }
    }, [navigate]);

    const fetchStudentClubs = async (studentId) => {
        try {
            const res = await fetch(`http://localhost:3001/api/students/${studentId}/clubs`); // Adjust the API endpoint
            if (res.ok) {
                const data = await res.json();
                setClubs(data.clubs);
            } else {
                const errorData = await res.json();
                setError(errorData.message || 'Failed to fetch clubs.');
            }
        } catch (error) {
            console.error('Error fetching student clubs:', error);
            setError('Error fetching clubs from the server.');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('studentId');
        localStorage.removeItem('studentName');
        navigate('/login-student');
    };

    return (
        <div className={styles.dashboardContainer}>
            {/* Sidebar */}
            <aside className={styles.sidebar}>
                <div className={styles.logoContainer}>
                    <img src={logo} alt="Skillers Logo" className={styles.logo} />
                </div>
                <nav className={styles.navigation}>
                    <ul>
                        <li>
                            <Link to="/student/dashboard" className={styles.navLink}>
                                <svg viewBox="0 0 24 24" fill="currentColor" className={styles.navIcon}> {/* Placeholder SVG */}
                                    <path d="M3 13h8V3H3v10zm0 8h8v-6H3v6zm10 0h8v-11h-8v11zm10-18v16h-8V3h8z" />
                                </svg>
                                Dashboard
                            </Link>
                        </li>
                        <li>
                            <Link to="/student/clubs" className={styles.navLink}>
                                <svg viewBox="0 0 24 24" fill="currentColor" className={styles.navIcon}> {/* Placeholder SVG */}
                                    <path d="M12 2c5.52 0 10 4.48 10 10s-4.48 10-10 10S2 17.52 2 12 6.48 2 12 2zm0 2c-4.41 0-8 3.59-8 8s3.59 8 8 8 8-3.59 8-8-3.59-8-8-8zm-1 13v-4h2v4h-2zm0-8v-2h2v2h-2z" />
                                </svg>
                                University Clubs
                            </Link>
                        </li>
                        <li>
                            <Link to="/student/events" className={styles.navLink}>
                                <svg viewBox="0 0 24 24" fill="currentColor" className={styles.navIcon}> {/* Placeholder SVG */}
                                    <path d="M19 3h-4V1h-2v2H9V1H7v2H3v18h18V3zm-2 16H5V8h14v11zm-7-2h4v-2h-4v2zm0-4h4v-2h-4v2z" />
                                </svg>
                                Upcoming Events
                            </Link>
                        </li>
                        <li>
                            <Link to="/student/profile" className={styles.navLink}>
                                <svg viewBox="0 0 24 24" fill="currentColor" className={styles.navIcon}> {/* Placeholder SVG */}
                                    <path d="M12 4a4 4 0 014 4 4 4 0 01-4 4 4 4 0 01-4-4 4 4 0 014-4zm0 10c4.42 0 8 1.79 8 4v2H4v-2c0-2.21 3.58-4 8-4z" />
                                </svg>
                                Profile
                            </Link>
                        </li>
                        <li>
                            <Link to="/student/settings" className={styles.navLink}>
                                <svg viewBox="0 0 24 24" fill="currentColor" className={styles.navIcon}> {/* Placeholder SVG */}
                                    <path d="M19.43 12.98c.04-.32.07-.64.07-.98s-.03-.66-.07-.98l2.11-1.81c.19-.17.2-.5.03-.69l-2.83-4.9c-.16-.28-.51-.36-.78-.24l-2.42 1.95c-.4-.3-.83-.58-1.29-.8l-.02-.02-1.95-2.42c-.12-.26-.46-.37-.7-.25l-4.9 2.83c-.17.17-.18.51-.02.69l2.11 1.81c-.04.32-.07.65-.07.98s.03.66.07.98l-2.11 1.81c-.16.17-.17.51.02.69l2.83 4.9c.16.27.51.35.78.24l2.42-1.95c.4.3.83.57 1.29.8l.02.02 1.95 2.42c.12.26.46.37.7.25l4.9-2.83c.17-.17.18-.51.02-.69l-2.11-1.81zM12 15.5c-1.93 0-3.5-1.57-3.5-3.5s1.57-3.5 3.5-3.5 3.5 1.57 3.5 3.5-1.57 3.5-3.5 3.5z" />
                                </svg>
                                Settings
                            </Link>
                        </li>
                        <li>
                            <Link to="/student/contact" className={styles.navLink}>
                                <svg viewBox="0 0 24 24" fill="currentColor" className={styles.navIcon}> {/* Placeholder SVG */}
                                    <path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 14H4V8l8 5 8-5v10zm-8-3L4 6h16L12 15z" />
                                </svg>
                                Contact
                            </Link>
                        </li>
                    </ul>
                </nav>
                <div className={styles.mobileApp}>
                    <p className={styles.mobileAppText}>Download our mobile app</p>
                    <svg viewBox="0 0 24 24" fill="currentColor" className={styles.mobileAppIcon}> {/* Placeholder SVG */}
                        <path d="M19 13h-6v6h-2v-6H5v-2h14v2zM12 3c-4.97 0-9 4.03-9 9s9 12 9 12 9-4.03 9-9-4.03-9-9-9zm0 2c3.87 0 7 3.13 7 7s-7 10-7 10-7-3.13-7-7 3.13-7 7-7z" />
                    </svg>
                </div>
            </aside>

            {/* Main Content Area */}
            <main className={styles.mainContent}>
                <header className={styles.header}>
                    <div className={styles.welcome}>
                        <h2 className={styles.welcomeTitle}>Welcome back, Alex!</h2>
                        <p className={styles.welcomeSubtitle}>You're doing great this week, keep it up!</p>
                    </div>
                    <div className={styles.searchBar}>
                        <input type="text" placeholder="What do you want to learn?" className={styles.searchInput} />
                        {/* Search Icon */}
                        <svg viewBox="0 0 24 24" fill="currentColor" className={styles.searchIcon}>
                            <path d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z" />
                        </svg>
                    </div>
                    <div className={styles.userProfile}>
                        <img src={userAvatar} alt="User Avatar" className={styles.avatar} />
                        {/* You might display the user's name here */}
                    </div>
                </header>

                <div className={styles.dashboardBodyContent}>
                    <Outlet /> {/* This is where the content of the specific sub-pages will be rendered */}
                </div>
            </main>
        </div>
    );
};

export default StudentDashboard;
