import React, { useState } from 'react';
import './ClubLogin.css';

const ClubLogin = () => {
  const [cid, setCid] = useState('');
  const [cpassword, setCpassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:3001/api/clubs/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ cid, cpassword })
      });

      const data = await res.json();

      if (res.ok) {
        alert('Login Successful');
        // You can redirect to club dashboard later
        window.location.href = '/club-dashboard';
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed (Frontend)');
    }
  };

  return (
    <div className="container">
      <img src="/images/oca.jpg" alt="OCA Logo" />
      <h2>Club Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <input type="number" name="cid" value={cid} onChange={(e) => setCid(e.target.value)} required placeholder=" " />
          <label>Enter Club ID</label>
        </div>
        <div className="input-group">
          <input type="password" name="cpassword" value={cpassword} onChange={(e) => setCpassword(e.target.value)} required placeholder=" " />
          <label>Enter your password</label>
        </div>
        <div className="options">
          <label>
            <input type="checkbox" /> Remember me
          </label>
          <a href="#">Forgot password?</a>
        </div>
        <button type="submit">Log In</button>
        <div className="register">
          <p>Back to <a href="/login-club">Login Selection</a></p>
        </div>
      </form>
    </div>
  );
};

export default ClubLogin;
