import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './ClubDashboard.module.css'; // Create this CSS module

const ClubDashboard = () => {
  const navigate = useNavigate();
  const [clubInfo, setClubInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/clubs/dashboard', {
          headers: {
            'Content-Type': 'application/json',
            // Cookies are automatically sent with cross-origin requests
            // when 'credentials: true' is set on the backend.
          },
        });

        if (!response.ok) {
          if (response.status === 401) {
            navigate('/login-club'); // Redirect to login if unauthorized
            return;
          }
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        setClubInfo(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [navigate]);

  const handleLogout = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/clubs/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        navigate('/login-club'); // Redirect to login after logout
      } else {
        const data = await response.json();
        alert(data.message || 'Logout failed');
      }
    } catch (err) {
      console.error('Logout error:', err);
      alert('Logout failed');
    }
  };

  if (loading) {
    return <div>Loading dashboard...</div>;
  }

  if (error) {
    return <div>Error loading dashboard: {error}</div>;
  }

  return (
    <div className={styles.dashboardContainer}>
      <header className={styles.dashboardHeader}>
        <h1>{clubInfo?.message || 'Club Dashboard'}</h1>
        {clubInfo?.clubId && <p>Club ID: {clubInfo.clubId}</p>}
        <button onClick={handleLogout} className={styles.logoutButton}>Logout</button>
      </header>

      <main className={styles.dashboardMain}>
        <section className={styles.dashboardSection}>
          <h2>Overview</h2>
          {/* Add club statistics and summary here */}
          <p>Welcome to your club's dashboard! Here you can manage your activities, members, and events.</p>
        </section>

        <section className={styles.dashboardSection}>
          <h2>Upcoming Events</h2>
          {/* List upcoming events */}
          <ul>
            <li>Event 1 - Date and Time</li>
            <li>Event 2 - Date and Time</li>
            {/* Fetch and map events from your backend */}
          </ul>
        </section>

        <section className={styles.dashboardSection}>
          <h2>Manage Members</h2>
          {/* Link to a members management page/section */}
          <p>Go to the members section to view and manage your club members.</p>
          <button>View Members</button>
        </section>

        {/* Add more sections for other club functionalities */}
      </main>

      <footer className={styles.dashboardFooter}>
        <p>&copy; {new Date().getFullYear()} Your Club Name</p>
      </footer>
    </div>
  );
};

export default ClubDashboard;
