import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StudentDashboard.module.css';
import { FaBook, FaCalendarCheck, FaUserCircle, FaBell, FaSearch, FaHome, FaUsers, FaCog, FaEnvelope } from 'react-icons/fa'; // More relevant icons

const StudentDashboard = () => {
    const navigate = useNavigate();
    const [studentName, setStudentName] = useState('');
    const [studentId, setStudentId] = useState('');
    const [clubs, setClubs] = useState([]);
    const [error, setError] = useState('');
    const [notifications, setNotifications] = useState([
        { id: 1, message: 'New course available!', time: '5 mins ago' },
        { id: 2, message: 'Upcoming club meeting', time: '1 hour ago' },
        // Add more notifications as needed
    ]);

    useEffect(() => {
        const storedStudentName = localStorage.getItem('studentName');
        const storedStudentId = localStorage.getItem('studentId');

        if (storedStudentName && storedStudentId) {
            setStudentName(storedStudentName ? storedStudentName.split(' ')[0] : 'Student');
            setStudentId(storedStudentId);
            fetchStudentClubs(storedStudentId);
        } else {
            navigate('/login-student');
        }
    }, [navigate]);

    const fetchStudentClubs = async (studentId) => {
        try {
            const res = await fetch(`http://localhost:3001/api/students/${studentId}/clubs`);
            if (res.ok) {
                const data = await res.json();
                setClubs(data.clubs);
            } else {
                const errorData = await res.json();
                setError(errorData.message || 'Failed to fetch clubs.');
            }
        } catch (error) {
            console.error('Error fetching student clubs:', error);
            setError('Error fetching clubs from the server.');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('studentId');
        localStorage.removeItem('studentName');
        navigate('/login-student');
    };

    return (
        <main className={styles.mainContent}>
            <div className={styles.dashboardContainerInner}>
                <header className={styles.header}>
                    <div className={styles.headerWelcome}>
                        Welcome Back, <span className={styles.headerUserName}>{studentName}</span>
                    </div>
                    <div className={styles.headerActions}>
                        <div className={styles.searchBar}>
                            <FaSearch className={styles.searchIcon} />
                            <input type="text" placeholder="Search Courses, Clubs..." className={styles.searchInput} />
                        </div>
                        <div className={styles.notificationIcon}>
                            <FaBell />
                            {notifications.length > 0 && <span className={styles.notificationBadge}>{notifications.length}</span>}
                        </div>
                        <div className={styles.userProfile}>
                            <FaUserCircle className={styles.userAvatarIcon} />
                            {/* You could add a dropdown here for profile options */}
                        </div>
                    </div>
                </header>

                <div className={styles.dashboardGrid}>
                    <div className={`${styles.dashboardCard}`}>
                        <div className={styles.cardHeader}>
                            <FaBook className={styles.cardHeaderIcon} />
                            <span className={styles.cardTitle}>Enrolled Courses</span>
                        </div>
                        <div className={styles.cardValue}>8</div> {/* Replace with actual count */}
                        <div className={styles.cardTrend}>View all courses <FaChevronRight className={styles.trendArrowIcon} /></div>
                    </div>

                    <div className={`${styles.dashboardCard}`}>
                        <div className={styles.cardHeader}>
                            <FaUsers className={styles.cardHeaderIcon} />
                            <span className={styles.cardTitle}>Your Clubs</span>
                        </div>
                        <div className={styles.cardValue}>{clubs.length}</div>
                        <div className={styles.cardTrend}>Explore clubs <FaChevronRight className={styles.trendArrowIcon} /></div>
                    </div>

                    <div className={`${styles.dashboardCard}`}>
                        <div className={styles.cardHeader}>
                            <FaCalendarCheck className={styles.cardHeaderIcon} />
                            <span className={styles.cardTitle}>Upcoming Events</span>
                        </div>
                        <div className={styles.cardValue}>2</div> {/* Replace with actual count */}
                        <div className={styles.cardTrend}>See event calendar <FaChevronRight className={styles.trendArrowIcon} /></div>
                    </div>

                    <div className={`${styles.dashboardCard} ${styles.quickLinksCard}`}>
                        <h2>Quick Links</h2>
                        <ul className={styles.quickLinksList}>
                            <li><Link to="/student/profile"><FaUserCircle className={styles.quickLinkIcon} /> Your Profile</Link></li>
                            <li><Link to="/student/settings"><FaCog className={styles.quickLinkIcon} /> Account Settings</Link></li>
                            <li><Link to="/student/clubs"><FaUsers className={styles.quickLinkIcon} /> Club Directory</Link></li>
                            <li><Link to="/student/events"><FaCalendarCheck className={styles.quickLinkIcon} /> Event Calendar</Link></li>
                            <li><Link to="/student/contact"><FaEnvelope className={styles.quickLinkIcon} /> Contact Support</Link></li>
                            <li><button onClick={handleLogout} className={styles.logoutButtonCard}><FaHome className={styles.quickLinkIcon} /> Logout</button></li>
                        </ul>
                    </div>

                    {/* You can add more relevant cards here, like recent activity, announcements, etc. */}
                </div>
            </div>
        </main>
    );
};

export default StudentDashboard;
