import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaTrash, FaEdit, FaPlusCircle, FaCheckCircle, FaArrowLeft } from 'react-icons/fa';
import './BudgetPage.css';

interface BudgetItem {
    _id: string;
    item_category: string;
    item_name: string;
    quantity: number;
    unit_price: number;
    event_name: string;
}

interface NewItem {
    item_category: string;
    item_name: string;
    quantity: number;
    unit_price: number;
    event_name: string;
}

const BudgetPage = () => {
    // 1. Use 'eventId' to match the route parameter in App.js
    const { eventId } = useParams();
    const navigate = useNavigate();
    const [budgetStatus, setBudgetStatus] = useState('pending');
    const [budgetItems, setBudgetItems] = useState([]);
    const [newItem, setNewItem] = useState({
        item_category: 'Logistics',
        item_name: '',
        quantity: 1,
        unit_price: 0,
        event_name: '',
    });
    const [editingItem, setEditingItem] = useState(null);
    const [totalBudget, setTotalBudget] = useState(0);
    const [clubName, setClubName] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchBudgetDetails = async () => {
            console.log('Frontend: Fetching budget details for eventId:', eventId)
            setLoading(true);
            try {
                // 2. Use 'eventId' here to fetch the budget for the event
                const response = await axios.get(`/api/budget/${eventId}`);
                if (response.data) {
                    setBudgetStatus(response.data.budgetStatus || 'pending');
                    setBudgetItems(response.data.budgetItems || []);
                    setTotalBudget(response.data.budget?.total_budget || 0);
                } else {
                    setErrorMessage('No budget data found.');
                    setBudgetStatus('pending');
                    setBudgetItems([]);
                    setTotalBudget(0);
                }

                const storedClubName = localStorage.getItem('clubName');
                const storedEventName = localStorage.getItem('currentEventName');
                setClubName(storedClubName || '');
                setNewItem(prev => ({ ...prev, event_name: storedEventName || '' }));
                setErrorMessage('');
            } catch (error) {
                console.error('Error fetching budget details:', error);
                setErrorMessage('Failed to load budget details.');
                setBudgetStatus('pending');
                setBudgetItems([]);
                setTotalBudget(0);
            } finally {
                setLoading(false);
            }
        };
        // 3.  Only fetch if eventId is defined.  Important!
        if (eventId) {
          fetchBudgetDetails();
        } else {
          setLoading(false); // set loading to false if there is no eventId
          setErrorMessage('Event ID is missing. Please check the URL.');
        }
    }, [eventId]); // 4. Depend on eventId

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setNewItem(prev => ({ ...prev, [name]: value }));
    };

    const handleAddItem = async (event) => {
        event.preventDefault();
        try {
            // 5.  Use eventId here, too.
            const response = await axios.post(`/api/budget/${eventId}/items`, newItem);
            setBudgetItems([...budgetItems, response.data.item]);
            setNewItem({ item_category: 'Logistics', item_name: '', quantity: 1, unit_price: 0, event_name: newItem.event_name });
            setErrorMessage('');
        } catch (error) {
            console.error('Error adding item:', error);
            setErrorMessage('Failed to add budget item.');
        }
    };

    const handleDeleteItem = async (itemId) => {
        if (window.confirm('Are you sure you want to delete this item?')) {
            try {
                await axios.delete(`/api/budget/items/${itemId}`);
                setBudgetItems(budgetItems.filter(item => item._id !== itemId));
                setErrorMessage('');
            } catch (error) {
                console.error('Error deleting item:', error);
                setErrorMessage('Failed to delete budget item.');
            }
        }
    };

    const handleEditItem = (item) => {
        setEditingItem({ ...item });
    };

    const handleEditInputChange = (event) => {
        const { name, value } = event.target;
        setEditingItem(prev => ({ ...prev, [name]: value }));
    };

    const handleUpdateItem = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.put(`/api/budget/items/${editingItem._id}`, {
                quantity: parseInt(editingItem.quantity),
                unit_price: parseFloat(editingItem.unit_price),
            });
            setBudgetItems(budgetItems.map(item =>
                item._id === response.data.item._id ? response.data.item : item
            ));
            setEditingItem(null);
            setErrorMessage('');
        } catch (error) {
            console.error('Error updating item:', error);
            setErrorMessage('Failed to update budget item.');
        }
    };

    const handleConfirmBudget = async () => {
        try {
             // 6.  Use eventId here
            const response = await axios.post(`/api/budget/${eventId}/confirm`, { club_name: clubName });
            setBudgetStatus('hold');
            setTotalBudget(response.data.totalBudget);
            setErrorMessage('');
        } catch (error) {
            console.error('Error confirming budget:', error);
            setErrorMessage('Failed to confirm budget.');
        }
    };

    const calculateTotalBudget = () => {
        return budgetItems.reduce((sum, item) => sum + item.quantity * item.unit_price, 0);
    };

    useEffect(() => {
        setTotalBudget(calculateTotalBudget());
    }, [budgetItems]);

    if (loading) {
        return <div>Loading budget details...</div>;
    }

    return (
        <div className="budget-page-container">
            <button onClick={() => navigate(-1)} className="back-button">
                <FaArrowLeft /> Back
            </button>
            <h2>Budget for Booking ID: {eventId}</h2>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            <p>Budget Status: {budgetStatus}</p>

            {budgetStatus === 'pending' && (
                <div className="add-item-form">
                    <h3>Add Budget Item</h3>
                    <form onSubmit={handleAddItem}>
                        <label htmlFor="item_category">Category:</label>
                        <select name="item_category" id="item_category" value={newItem.item_category} onChange={handleInputChange} required>
                            <option value="Logistics">Logistics</option>
                            <option value="Guest Food">Guest Food</option>
                            <option value="Visitor Food">Visitor Food</option>
                            <option value="Transport Cost">Transport Cost</option>
                            <option value="Miscellaneous">Miscellaneous</option>
                        </select>

                        <label htmlFor="item_name">Item Name:</label>
                        <input type="text" name="item_name" id="item_name" value={newItem.item_name} onChange={handleInputChange} required />

                        <label htmlFor="quantity">Quantity:</label>
                        <input type="number" name="quantity" id="quantity" value={newItem.quantity} onChange={handleInputChange} required min="1" />

                        <label htmlFor="unit_price">Unit Price:</label>
                        <input type="number" step="0.01" name="unit_price" id="unit_price" value={newItem.unit_price} onChange={handleInputChange} required min="0" />

                        <button type="submit"><FaPlusCircle /> Add Item</button>
                    </form>
                </div>
            )}

            <h3>Budget Items</h3>
            {budgetItems && budgetItems.length === 0 ? (
                <p>No budget items added yet.</p>
            ) : (
                <table className="budget-items-table">
                    <thead>
                        <tr>
                            <th>Category</th>
                            <th>Item Name</th>
                            <th>Quantity</th>
                            <th>Unit Price</th>
                            <th>Total Price</th>
                            {budgetStatus === 'pending' && <th>Actions</th>}
                        </tr>
                    </thead>
                    <tbody>
                        {budgetItems && budgetItems.map(item => (
                            <tr key={item._id}>
                                <td>{item.item_category}</td>
                                <td>{item.item_name}</td>
                                <td>{item.quantity}</td>
                                <td>{item.unit_price}</td>
                                <td>{(item.quantity * item.unit_price).toFixed(2)}</td>
                                {budgetStatus === 'pending' && (
                                    <td>
                                        <button className="edit-button" onClick={() => handleEditItem(item)}>
                                            <FaEdit /> Edit
                                        </button>
                                        <button className="delete-button" onClick={() => handleDeleteItem(item._id)}>
                                            <FaTrash /> Delete
                                        </button>
                                    </td>
                                )}
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}

            <h3>Total Budget: ${totalBudget.toFixed(2)}</h3>

            {budgetStatus === 'pending' && (
                <button onClick={handleConfirmBudget} className="confirm-button">
                    <FaCheckCircle /> Confirm Budget
                </button>
            )}

            {editingItem && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close-button" onClick={() => setEditingItem(null)}>&times;</span>
                        <h3>Edit Item</h3>
                        <form onSubmit={handleUpdateItem}>
                            <label htmlFor={`edit_quantity_${editingItem._id}`}>Quantity:</label>
                            <input
                                type="number"
                                name="quantity"
                                id={`edit_quantity_${editingItem._id}`}
                                value={editingItem.quantity}
                                onChange={handleEditInputChange}
                                required
                                min="1"
                            />

                            <label htmlFor={`edit_unit_price_${editingItem._id}`}>Unit Price:</label>
                            <input
                                type="number"
                                step="0.01"
                                name="unit_price"
                                id={`edit_unit_price_${editingItem._id}`}
                                value={editingItem.unit_price}
                                onChange={handleEditInputChange}
                                required
                                min="0"
                            />
                            <button type="submit"><FaCheckCircle /> Update Item</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default BudgetPage;
