import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';



const ClubRecruitments = () => {
  const navigate = useNavigate();
  const [recruitments, setRecruitments] = useState([]);
  const [deadline, setDeadline] = useState('');
  const [description, setDescription] = useState('');
  const [club, setClub] = useState(null);
  const currentSemester = 'Spring 2025'; // Change dynamically as needed

  useEffect(() => {
    axios.get('http://localhost:3001/api/clubs/dashboard').then(res => {
      setClub(res.data.clubDetails);
      axios.get(`http://localhost:3001/api/recruitment/${res.data.clubDetails._id}`).then(r => {
        setRecruitments(r.data);
      });
    });
  }, []);

  const startRecruitment = () => {
    axios.post('http://localhost:3001/api/recruitment/create', {
      clubId: club._id,
      clubName: club.cname,
      semester: currentSemester,
      application_deadline: deadline,
      description
    }).then(res => {
      alert(res.data.message);
      setRecruitments(prev => [res.data.recruitment, ...prev]);
    });
  };

  const stopRecruitment = (semester) => {
    axios.put('http://localhost:3001/api/recruitment/stop', {
      clubId: club._id,
      semester
    }).then(() => {
      setRecruitments(prev => prev.map(r => r.semester === semester ? { ...r, status: 'no' } : r));
    });
  };

  return (
    <div>
      <h2>Recruitment Page</h2>
      <p>Club Name: {club?.cname}</p>
      <p>Club ID: {club?.cid}</p>
      <p>Current Semester: {currentSemester}</p>

      <h4>Status: {recruitments.find(r => r.semester === currentSemester)?.status === 'yes' ? 'Recruiting' : 'Not Recruiting'}</h4>

      <input type="date" value={deadline} onChange={e => setDeadline(e.target.value)} />
      <textarea value={description} onChange={e => setDescription(e.target.value)} placeholder="Write recruitment description" />
      <button onClick={startRecruitment}>Start Recruitment</button>

      <hr />

      <h3>All Recruitment Semesters</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {recruitments.map(rec => (
          <div key={rec.semester} style={{ border: '1px solid #ccc', padding: 12, margin: 10, width: 250 }}>
            <h4>{rec.semester}</h4>
            <p>Status: {rec.status}</p>
            <p>Pending: {rec.pending_std.length}</p>
            <p>Approved: {rec.approved_std.length}</p>
            <p>Rejected: {rec.rejected_std.length}</p>

            <button onClick={() => navigate(`/club/recruitments/evaluate/${rec.semester}`)}>
  Evaluate Applicants
</button>
            {rec.status === 'yes' && (
              <button onClick={() => stopRecruitment(rec.semester)}>Stop Recruitment</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ClubRecruitments;
