// src/pages/StudentClubsPage.jsx

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const StudentClubsPage = () => {
  const [clubs, setClubs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('/clubs/all')
      .then(res => res.json())
      .then(data => setClubs(data))
      .catch(err => console.error('Error fetching clubs:', err));
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">All University Clubs</h1>

      <div className="mb-6 flex gap-4">
        <button onClick={() => navigate('/student/myclubs')} className="bg-blue-600 text-white px-4 py-2 rounded">
          My Clubs
        </button>
        <button onClick={() => navigate('/student/joinclubs')} className="bg-green-600 text-white px-4 py-2 rounded">
          Join Clubs
        </button>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {clubs.map((club) => (
          <div key={club._id} className="p-4 border rounded shadow bg-white">
            <img src={club.clogo || 'https://via.placeholder.com/100'} alt="Logo" className="w-20 h-20 mb-2" />
            <h2 className="text-lg font-semibold">{club.cname}</h2>
            <p className="text-sm">{club.cdescription || 'No description available.'}</p>
            <p className="text-xs text-gray-500 mt-1">Short Name: {club.cshortname}</p>
            <p className="text-xs text-gray-500">Email: {club.cmail}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StudentClubsPage;
