import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StudentDashboard.module.css'; // Create this CSS file

const StudentDashboard = () => {
    const navigate = useNavigate();
    const [studentName, setStudentName] = useState('');
    const [studentId, setStudentId] = useState('');
    const [clubs, setClubs] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const storedStudentName = localStorage.getItem('studentName');
        const storedStudentId = localStorage.getItem('studentId');

        if (storedStudentName && storedStudentId) {
            setStudentName(storedStudentName);
            setStudentId(storedStudentId);
            fetchStudentClubs(storedStudentId); // Fetch clubs upon component mount
        } else {
            // If no student info in local storage, redirect to login
            navigate('/login-student');
        }
    }, [navigate]);

    const fetchStudentClubs = async (studentId) => {
        try {
            const res = await fetch(`http://localhost:3001/api/students/${studentId}/clubs`); // Adjust the API endpoint
            if (res.ok) {
                const data = await res.json();
                setClubs(data.clubs);
            } else {
                const errorData = await res.json();
                setError(errorData.message || 'Failed to fetch clubs.');
            }
        } catch (error) {
            console.error('Error fetching student clubs:', error);
            setError('Error fetching clubs from the server.');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('studentId');
        localStorage.removeItem('studentName');
        navigate('/login-student');
    };

    return (
        <div className={styles.dashboardBody}>
            <div className={styles.dashboardContainer}>
                <h2 className={styles.dashboardTitle}>Welcome, {studentName}!</h2>
                <p className={styles.dashboardInfo}>Student ID: {studentId}</p>

                <div className={styles.clubsSection}>
                    <h3>Your Clubs</h3>
                    {error && <p className={styles.error}>{error}</p>}
                    {clubs.length > 0 ? (
                        <ul className={styles.clubsList}>
                            {clubs.map((club, index) => (
                                <li key={index}>{club}</li>
                            ))}
                        </ul>
                    ) : (
                        <p>You are not currently enrolled in any clubs.</p>
                    )}
                </div>

                <button onClick={handleLogout} className={styles.logoutButton}>Logout</button>
            </div>
        </div>
    );
};

export default StudentDashboard;
