import React, { useState, useEffect } from 'react';
import styles from './StudentDashboard.module.css';
import { FaCalendarAlt, FaUsers, FaUserCircle, FaBell, FaLightbulb } from 'react-icons/fa';

const StudentDashboard = () => {
    const [studentName, setStudentName] = useState('');
    const [studentId, setStudentId] = useState('');
    const [upcomingEvents, setUpcomingEvents] = useState([]);
    const [enrolledClubs, setEnrolledClubs] = useState([]);
    const [latestAnnouncements, setLatestAnnouncements] = useState([]);
    const [profileInfo, setProfileInfo] = useState({}); // Placeholder for profile details

    useEffect(() => {
        const storedStudentName = localStorage.getItem('studentName');
        const storedStudentId = localStorage.getItem('studentId');

        if (storedStudentName && storedStudentId) {
            setStudentName(storedStudentName ? storedStudentName.split(' ')[0] : 'Student');
            setStudentId(storedStudentId);
            fetchStudentDashboardData(storedStudentId);
        }
    }, []);

    const fetchStudentDashboardData = async (studentId) => {
        // Replace with your actual API calls
        // Example:
        // try {
        //     const response = await fetch(`http://localhost:3001/api/students/${studentId}/dashboard`);
        //     if (response.ok) {
        //         const data = await response.json();
        //         setUpcomingEvents(data.upcomingEvents);
        //         setEnrolledClubs(data.enrolledClubs);
        //         setLatestAnnouncements(data.latestAnnouncements);
        //         setProfileInfo(data.profileInfo);
        //     }
        // } catch (error) {
        //     console.error("Error fetching student dashboard data:", error);
        // }

        // Dummy data for demonstration
        setUpcomingEvents([
            { id: 1, title: 'Club Fair', date: 'May 6, 2025', location: 'Main Hall' },
            { id: 2, title: 'Student Union Meeting', date: 'May 9, 2025', time: '3:00 PM' },
        ]);
        setEnrolledClubs(['Photography Club', 'Music Society']);
        setLatestAnnouncements([
            { id: 1, title: 'Call for Volunteers - Campus Cleanup', date: 'May 5, 2025' },
            { id: 2, title: 'New Club Added: Robotics Club', date: 'May 4, 2025' },
        ]);
        setProfileInfo({
            major: 'Computer Science',
            studentId: studentId,
            // Add more profile details as needed
        });
    };

    return (
        <main className={styles.mainContent}>
            <div className={styles.dashboardContainerInner}>
                <header className={styles.header}>
                    <div className={styles.headerWelcome}>
                        Welcome Back, <span className={styles.headerUserName}>{studentName}</span>!
                    </div>
                    <div className={styles.headerActions}>
                        <div className={styles.notificationBell}>
                            <FaBell className={styles.bellIcon} />
                            {latestAnnouncements.length > 0 && <span className={styles.notificationBadge}>{latestAnnouncements.length}</span>}
                        </div>
                        <div className={styles.userAvatar}>
                            <FaUserCircle className={styles.avatarIcon} />
                        </div>
                    </div>
                </header>

                <div className={styles.dashboardGrid}>
                    {/* Upcoming Events */}
                    <div className={styles.eventsCard}>
                        <h2 className={styles.cardTitle}><FaCalendarAlt /> Upcoming Events</h2>
                        {upcomingEvents.length > 0 ? (
                            <ul className={styles.eventList}>
                                {upcomingEvents.map(event => (
                                    <li key={event.id} className={styles.eventItem}>
                                        <span className={styles.eventName}>{event.title}</span>
                                        <span className={styles.eventDetails}>
                                            {event.date && <span className={styles.detailItem}>{event.date}</span>}
                                            {event.time && <span className={styles.detailItem}>{event.time}</span>}
                                            {event.location && <span className={styles.detailItem}>{event.location}</span>}
                                        </span>
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p className={styles.noData}>No upcoming events.</p>
                        )}
                        <button className={styles.viewMoreButton}>View All Events</button>
                    </div>

                    {/* Your Enrolled Clubs */}
                    <div className={styles.clubsCard}>
                        <h2 className={styles.cardTitle}><FaUsers /> Your Clubs</h2>
                        {enrolledClubs.length > 0 ? (
                            <div className={styles.clubList}>
                                {enrolledClubs.map(club => (
                                    <span key={club} className={styles.clubBadge}>{club}</span>
                                ))}
                            </div>
                        ) : (
                            <p className={styles.noData}>Not enrolled in any clubs yet.</p>
                        )}
                        <button className={styles.viewMoreButton}>Explore Clubs</button>
                    </div>

                    {/* Profile Snapshot */}
                    <div className={styles.profileCard}>
                        <h2 className={styles.cardTitle}><FaUserCircle /> Your Profile</h2>
                        <div className={styles.profileInfo}>
                            <p><span className={styles.infoLabel}>Student ID:</span> {profileInfo.studentId || 'N/A'}</p>
                            <p><span className={styles.infoLabel}>Major:</span> {profileInfo.major || 'N/A'}</p>
                            {/* Add more profile information here */}
                        </div>
                        <button className={styles.viewMoreButton}>View Profile</button>
                    </div>

                    {/* Latest Announcements */}
                    <div className={styles.announcementsCard}>
                        <h2 className={styles.cardTitle}><FaLightbulb /> Latest Announcements</h2>
                        <ul className={styles.announcementsList}>
                            {latestAnnouncements.map(announcement => (
                                <li key={announcement.id} className={styles.announcementItem}>
                                    <span className={styles.announcementTitle}>{announcement.title}</span>
                                    <span className={styles.announcementDate}>{announcement.date}</span>
                                </li>
                            ))}
                            {latestAnnouncements.length === 0 && <li className={styles.announcementItem}>No new announcements.</li>}
                        </ul>
                        <button className={styles.viewAllButton}>View All Announcements</button>
                    </div>

                    {/* You can add other relevant sections here */}
                </div>
            </div>
        </main>
    );
};

export default StudentDashboard;
