import React, { useState, useEffect } from 'react';
import { useNavigate, Link, Outlet } from 'react-router-dom';
import styles from './StudentDashboard.module.css';
import {
    AiOutlineHome,
    AiOutlineBook,
    AiOutlineCalendar,
    AiOutlineUser,
    AiOutlineSetting,
    AiOutlineMail,
    AiOutlineSearch,
    AiOutlineDownload
} from 'react-icons/ai';
import logo from 'assets/skillers_logo.svg';
import userAvatar from 'assets/default_avatar.png'; // Now one level up from src/students/ // Replace with your actual user avatar path

const StudentDashboard = () => {
    const navigate = useNavigate();
    const [studentName, setStudentName] = useState('');
    const [studentId, setStudentId] = useState('');
    const [clubs, setClubs] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const storedStudentName = localStorage.getItem('studentName');
        const storedStudentId = localStorage.getItem('studentId');

        if (storedStudentName && storedStudentId) {
            setStudentName(storedStudentName ? storedStudentName.split(' ')[0] : 'Student'); // Just get the first name for the welcome message
            setStudentId(storedStudentId);
            fetchStudentClubs(storedStudentId);
        } else {
            navigate('/login-student');
        }
    }, [navigate]);

    const fetchStudentClubs = async (studentId) => {
        try {
            const res = await fetch(`http://localhost:3001/api/students/${studentId}/clubs`);
            if (res.ok) {
                const data = await res.json();
                setClubs(data.clubs);
            } else {
                const errorData = await res.json();
                setError(errorData.message || 'Failed to fetch clubs.');
            }
        } catch (error) {
            console.error('Error fetching student clubs:', error);
            setError('Error fetching clubs from the server.');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('studentId');
        localStorage.removeItem('studentName');
        navigate('/login-student');
    };

    return (
        <div className={styles.dashboardContainer}>
            {/* Sidebar */}
            <aside className={styles.sidebar}>
                <div className={styles.logoContainer}>
                    <img src={logo} alt="Skillers Logo" className={styles.logo} />
                </div>
                <nav className={styles.navigation}>
                    <ul>
                        <li>
                            <Link to="/student/dashboard" className={styles.navLink}>
                                <AiOutlineHome className={styles.navIcon} />
                                Dashboard
                            </Link>
                        </li>
                        <li>
                            <Link to="/student/clubs" className={styles.navLink}>
                                <AiOutlineBook className={styles.navIcon} />
                                University Clubs
                            </Link>
                        </li>
                        <li>
                            <Link to="/student/events" className={styles.navLink}>
                                <AiOutlineCalendar className={styles.navIcon} />
                                Upcoming Events
                            </Link>
                        </li>
                        <li>
                            <Link to="/student/profile" className={styles.navLink}>
                                <AiOutlineUser className={styles.navIcon} />
                                Profile
                            </Link>
                        </li>
                        <li>
                            <Link to="/student/settings" className={styles.navLink}>
                                <AiOutlineSetting className={styles.navIcon} />
                                Settings
                            </Link>
                        </li>
                        <li>
                            <Link to="/student/contact" className={styles.navLink}>
                                <AiOutlineMail className={styles.navIcon} />
                                Contact
                            </Link>
                        </li>
                    </ul>
                </nav>
                <div className={styles.mobileApp}>
                    <p className={styles.mobileAppText}>Download our mobile app</p>
                    <AiOutlineDownload className={styles.mobileAppIcon} />
                </div>
            </aside>

            {/* Main Content Area */}
            <main className={styles.mainContent}>
                <header className={styles.header}>
                    <div className={styles.welcome}>
                        <h2 className={styles.welcomeTitle}>Welcome back, {studentName}!</h2>
                        <p className={styles.welcomeSubtitle}>You're doing great this week, keep it up!</p>
                    </div>
                    <div className={styles.searchBar}>
                        <AiOutlineSearch className={styles.searchIcon} />
                        <input type="text" placeholder="What do you want to learn?" className={styles.searchInput} />
                    </div>
                    <div className={styles.userProfile}>
                        <img src={userAvatar} alt="User Avatar" className={styles.avatar} />
                        {/* You might display a dropdown or more user info here */}
                    </div>
                </header>

                <div className={styles.dashboardBodyContent}>
                    {/* Placeholder for the main dashboard content */}
                    <div className={styles.statisticsSection}>
                        <div className={styles.statisticCard}>
                            <span className={styles.statisticNumber}>2</span>
                            <span className={styles.statisticLabel}>Courses completed</span>
                        </div>
                        <div className={styles.statisticCard}>
                            <span className={styles.statisticNumber}>4</span>
                            <span className={styles.statisticLabel}>Courses in progress</span>
                        </div>
                        <div className={styles.statisticCard}>
                            <span className={styles.statisticNumber}>7.5</span>
                            <span className={styles.statisticLabel}>Hours spent this week</span>
                            {/* You might add a small graph here */}
                        </div>
                    </div>

                    <div className={styles.continueWatchingSection}>
                        <h3 className={styles.sectionTitle}>Continue to watch</h3>
                        <div className={styles.courseCards}>
                            <div className={styles.courseCard}>
                                <div className={styles.courseImagePlaceholder}></div>
                                <span className={styles.courseTitle}>Prototyping and testing</span>
                            </div>
                            <div className={styles.courseCard}>
                                <div className={styles.courseImagePlaceholder}></div>
                                <span className={styles.courseTitle}>Finding your style</span>
                            </div>
                            {/* Add more "Continue watching" cards as needed */}
                        </div>
                    </div>

                    <div className={styles.yourCoursesSection}>
                        <h3 className={styles.sectionTitle}>Your courses</h3>
                        <div className={styles.courseCardsGrid}>
                            <div className={styles.courseCardLarge}>
                                <div className={styles.courseImageLargePlaceholder}></div>
                                <div className={styles.courseDetails}>
                                    <span className={styles.courseTitleLarge}>Mobile app design</span>
                                    <span className={styles.courseAuthor}>by Simpla Studio</span>
                                    <div className={styles.progressBar}>
                                        <div className={styles.progressBarFill} style={{ width: '26%' }}></div>
                                        <span className={styles.progressPercentage}>26%</span>
                                    </div>
                                    <span className={styles.courseProgressText}>12/45 lessons</span>
                                    <span className={styles.courseProgressText}>4/15 tasks</span>
                                </div>
                            </div>
                            <div className={styles.courseCardLarge}>
                                <div className={styles.courseImageLargePlaceholder}></div>
                                <div className={styles.courseDetails}>
                                    <span className={styles.courseTitleLarge}>Illustration</span>
                                    <span className={styles.courseAuthor}>by Mia Anderson</span>
                                    <div className={styles.progressBar}>
                                        <div className={styles.progressBarFill} style={{ width: '65%' }}></div>
                                        <span className={styles.progressPercentage}>65%</span>
                                    </div>
                                    <span className={styles.courseProgressText}>24/37 lessons</span>
                                    <span className={styles.courseProgressText}>8/12 tasks</span>
                                </div>
                            </div>
                            <div className={styles.courseCardLarge}>
                                <div className={styles.courseImageLargePlaceholder}></div>
                                <div className={styles.courseDetails}>
                                    <span className={styles.courseTitleLarge}>UX/UI design</span>
                                    <span className={styles.courseAuthor}>by Horizon</span>
                                    <div className={styles.progressBar}>
                                        <div className={styles.progressBarFill} style={{ width: '100%' }}></div>
                                        <span className={styles.progressPercentage}>100%</span>
                                    </div>
                                    <span className={styles.courseProgressText}>51/51 lessons</span>
                                    <span className={styles.courseProgressText}>23/23 tasks</span>
                                </div>
                            </div>
                            {/* Add more "Your courses" cards */}
                        </div>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default StudentDashboard;
