import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import {
  FaUserGraduate, FaClock, FaCheckCircle, FaTimesCircle, FaSpinner,
  FaSearch, FaEnvelope, FaPhone, FaUser, FaVenusMars, FaBook,
  FaCalendarAlt, FaExclamationTriangle, FaSave, FaBuilding, FaUsers
} from 'react-icons/fa';
import styles from './EvaluateApplicants.module.css';

// Updated StatusBadge component with new class names
const StatusBadge = ({ status }) => {
  let icon;
  let text;
  let badgeClass;

  switch (status) {
    case 'approved':
      icon = <FaCheckCircle />;
      text = 'Approved';
      badgeClass = styles.evaluateStatusApproved;
      break;
    case 'rejected':
      icon = <FaTimesCircle />;
      text = 'Rejected';
      badgeClass = styles.evaluateStatusRejected;
      break;
    case 'pending':
    default:
      icon = <FaClock />;
      text = 'Pending';
      badgeClass = styles.evaluateStatusPending;
      break;
  }

  return (
    <span className={`${styles.evaluateStatusBadge} ${badgeClass}`}>
      {icon} {text}
    </span>
  );
};

const EvaluateApplicants = () => {
  // ... (keep all your existing state and effect hooks)

  // Render Loading State
  if (loading) {
    return (
      <div className={styles.evaluateLoadingState}>
        <FaSpinner className={styles.evaluateLoadingSpinner} />
        <p className={styles.evaluateLoadingMessage}>Loading Applicant Data...</p>
      </div>
    );
  }

  // Render Error State
  if (error && !club) {
    return (
      <div className={styles.evaluateErrorState}>
        <FaExclamationTriangle className={styles.evaluateErrorIcon} />
        <p className={styles.evaluateErrorMessage}>{error}</p>
        <button onClick={fetchData} className={styles.evaluateRetryButton}>Retry</button>
      </div>
    );
  }

  // Main Render
  return (
    <div className={styles.evaluateApplicantsContainer}>
      <header className={styles.evaluateHeader}>
        <div className={styles.evaluateTitleWrapper}>
          <FaUserGraduate size={30} />
          <h1 className={styles.evaluateTitle}>Evaluate Applicants</h1>
          <span className={styles.evaluateSemesterBadge}>{semester}</span>
        </div>
        <div className={styles.evaluateClubInfo}>
          <FaBuilding /> {club?.cname || 'Club Name Unavailable'}
        </div>
        {error && (
          <div className={styles.evaluateInlineError}>
            <FaExclamationTriangle /> {error}
          </div>
        )}
      </header>

      <div className={styles.evaluateControls}>
        <div className={styles.evaluateSearchWrapper}>
          <FaSearch className={styles.evaluateSearchIcon} />
          <input
            type="text"
            placeholder="Search by Name or ID..."
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
            className={styles.evaluateSearchInput}
          />
        </div>
        <div className={styles.evaluateStats}>
          <span className={styles.evaluateStatItem}>
            <FaClock /> Pending: <strong className={styles.evaluateStatValue}>{counts.pending}</strong>
          </span>
          <span className={styles.evaluateStatItem}>
            <FaCheckCircle /> Approved: <strong className={styles.evaluateStatValue}>{counts.approved}</strong>
          </span>
          <span className={styles.evaluateStatItem}>
            <FaTimesCircle /> Rejected: <strong className={styles.evaluateStatValue}>{counts.rejected}</strong>
          </span>
          <span className={styles.evaluateStatItem}>
            <FaUsers /> Total: <strong className={styles.evaluateStatValue}>{applicants.length}</strong>
          </span>
        </div>
      </div>

      <div className={styles.evaluateApplicantsList}>
        {filteredApplicants.length > 0 ? (
          filteredApplicants.map(app => {
            const isUpdating = updatingStatus[app.uid];
            const hasChanged = app.status !== app.selectedStatus;
            
            return (
              <div 
                key={app.uid} 
                className={`${styles.evaluateApplicantCard} ${isUpdating ? styles.evaluateCardUpdating : ''}`}
              >
                <div className={styles.evaluateCardHeader}>
                  <h3 className={styles.evaluateApplicantName}>{app.uname || 'Unnamed Applicant'}</h3>
                  <span className={styles.evaluateApplicantID}>ID: {app.uid}</span>
                </div>

                <div className={styles.evaluateCardBody}>
                  <p><FaEnvelope className={styles.evaluateInfoIcon} /> {app.umail || 'N/A'}</p>
                  <p><FaPhone className={styles.evaluateInfoIcon} /> {app.umobile || 'N/A'}</p>
                  <p><FaVenusMars className={styles.evaluateInfoIcon} /> {app.ugender || 'N/A'}</p>
                  <p><FaBook className={styles.evaluateInfoIcon} /> {app.major || 'N/A'}</p>
                  <p><FaCalendarAlt className={styles.evaluateInfoIcon} /> {app.semester || 'N/A'}</p>
                </div>

                <div className={styles.evaluateCardStatus}>
                  <strong className={styles.evaluateStatusLabel}>Current Status:</strong> 
                  <StatusBadge status={app.status} />
                </div>

                <div className={styles.evaluateCardActions}>
                  <select
                    value={app.selectedStatus}
                    onChange={e => handleStatusChange(app.uid, e.target.value)}
                    className={styles.evaluateStatusSelect}
                    disabled={isUpdating}
                  >
                    <option value="pending">Pending</option>
                    <option value="approved">Approved</option>
                    <option value="rejected">Rejected</option>
                  </select>
                  <button
                    onClick={() => handleSubmitUpdate(app.uid)}
                    className={styles.evaluateActionButton}
                    disabled={isUpdating || !hasChanged}
                  >
                    {isUpdating ? (
                      <FaSpinner className={styles.evaluateButtonSpinner} />
                    ) : (
                      <FaSave />
                    )}
                    {isUpdating ? 'Updating...' : 'Update Status'}
                  </button>
                </div>
              </div>
            );
          })
        ) : (
          <div className={styles.evaluateEmptyState}>
            <FaUserGraduate size={50} color="#ccc" />
            <p className={styles.evaluateEmptyMessage}>
              No applicants found{searchTerm ? ' matching your search criteria' : ' for this semester'}.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default EvaluateApplicants;
