import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './ClubRecruitments.module.css'; // Import the CSS module
//import { Button } from '@/components/ui/button'; // Remove
//import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'; // Remove
//import { Badge } from '@/components/ui/badge';  // Remove
//import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'; // Remove
//import { cn } from 'class-variance-authority';  // Remove

const ClubRecruitments = () => {
const { clubId } = useParams();
const [club, setClub] = useState(null);
const [semesterData, setSemesterData] = useState([]);
const [loading, setLoading] = useState(true);
const [error, setError] = useState('');
const [selectedSemesters, setSelectedSemesters] = useState([]);
const [recruitmentStatus, setRecruitmentStatus] = useState('');
const navigate = useNavigate();
const [isOpen, setIsOpen] = useState(false);
const selectRef = useRef(null);


useEffect(() => {
    const handleClickOutside = (event) => {
        if (selectRef.current && !selectRef.current.contains(event.target)) {
            setIsOpen(false);
        }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
        document.removeEventListener('mousedown', handleClickOutside);
    };
}, []);

useEffect(() => {
const fetchClubData = async () => {
    try {
    setLoading(true);
    const response = await axios.get(`http://localhost:3001/api/clubs/details/${clubId}`);
    setClub(response.data.club);
    setSemesterData(response.data.semesterData);
            setRecruitmentStatus(response.data.club.creq);
    } catch (err) {
    setError(err.message || 'Failed to fetch club data.');
    } finally {
    setLoading(false);
    }
};

fetchClubData();
}, [clubId]);

const handleSemesterChange = (value) => {
    setSelectedSemesters(value);
    setIsOpen(false); // Close dropdown after selection
};

const handleStartRecruitment = async () => {
try {
    if (!selectedSemesters || selectedSemesters.length === 0) {
    setError('Please select at least one semester.');
    return;
    }
    setError('');
    const response = await axios.post(`http://localhost:3001/api/clubs/set-recruitment/${clubId}`, {
    semester: selectedSemesters,
            recruitmentStatus: 'Yes'
    });
    setClub(response.data.club);
        setRecruitmentStatus(response.data.club.creq);
    setSemesterData(prevData => {
            // If semester data already exists, update it.  If not, create it.
            const newSemesterData = selectedSemesters.map(sem => {
                const existing = prevData.find(item => item.semester === sem);
                return existing ? existing : {
                    semester: sem,
                    totalApplicants: 0,
                    pending: 0,
                    accepted: 0,
                    rejected: 0
                };
            });
            return newSemesterData;
        });

} catch (err) {
    setError(err.response?.data?.message || 'Failed to start recruitment.');
}
};

const handleEndRecruitment = async() => {
        try {

    const response = await axios.post(`http://localhost:3001/api/clubs/set-recruitment/${clubId}`, {
    semester: [], //clear semester
        recruitmentStatus: 'No'
    });
    setClub(response.data.club);
        setRecruitmentStatus(response.data.club.creq);
    setSemesterData([]);

} catch (err) {
    setError(err.response?.data?.message || 'Failed to end recruitment.');
}
}

const handleEvaluation = (semester) => {
navigate(`/club/${clubId}/evaluation/${semester}`);
};

if (loading) {
return <div>Loading...</div>; // Replace with a proper loading indicator
}

if (error) {
return <div className="text-red-500">{error}</div>;
}

if (!club) {
return <div>Club not found.</div>;
}

return (
<div className={styles.recruitmentsPage}>
    <h2 className={styles.heading}>Club Recruitment: {club.cname}</h2>
    <p className={styles.clubInfo}>Club ID: {club.cid}</p>
    <p className={styles.status}>Recruitment Status: {recruitmentStatus}</p>

    {recruitmentStatus === 'No' ? (
    <div className={styles.semesterSelect}>
        <div ref={selectRef} className="relative">
        <div
            className={styles.selectTrigger}
            onClick={() => setIsOpen(!isOpen)}
        >
            <span className={styles.selectValue}>
                {selectedSemesters.length > 0
                    ? selectedSemesters.join(', ')
                    : 'Select Semesters'}
            </span>
            {isOpen ? '▲' : '▼'}
        </div>
        {isOpen && (
            <div className={styles.selectContent}>
                {['1st', '2nd', '3rd', '4th', '5th', '6th', '7th', '8th'].map((semester) => (
                    <div
                        key={semester}
                        className={styles.selectItem}
                        onClick={() => handleSemesterChange([...selectedSemesters, semester])}
                    >
                        {semester}
                    </div>
                ))}
            </div>
        )}
        </div>
        <button onClick={handleStartRecruitment} className={styles.evaluateButton}>
        Start Recruitment
        </button>
    </div>
    ) : (
    <div className="mt-4">
        <button onClick={handleEndRecruitment} className={styles.evaluateButton}>End Recruitment</button>
    </div>
    )}

    <h3 className={styles.heading}>Recruiting Semesters</h3>
    <div className={styles.semesterGrid}>
    {semesterData.map((semesterInfo) => (
        <div key={semesterInfo.semester} className={styles.semesterCard}>
        <h4 className={styles.semesterCardTitle}>{semesterInfo.semester}</h4>
        <p className={styles.semesterCardDescription}>Recruitment Details</p>
        <div className={styles.applicantInfo}>
            <p>Total Applicants: <span className={`${styles.badge} ${styles.badgeSecondary}`}>{semesterInfo.totalApplicants}</span></p>
            <p>Pending: <span className={`${styles.badge} ${styles.badgeOutline}`}>{semesterInfo.pending}</span></p>
            <p>Accepted: <span className={`${styles.badge} ${styles.badgeGreen}`}>{semesterInfo.accepted}</span></p>
            <p>Rejected: <span className={`${styles.badge} ${styles.badgeRed}`}>{semesterInfo.rejected}</span></p>
            <button onClick={() => handleEvaluation(semesterInfo.semester)} className={styles.evaluateButton}>
            Evaluate
            </button>
        </div>
        </div>
    ))}
    </div>
</div>
);
};

export default ClubRecruitments;
