import React, { useState, useEffect } from 'react';
import styles from './StudentDashboard.module.css';
import { FaBookOpen, FaCalendarAlt, FaUserCircle, FaChartLine, FaBell, FaLightbulb, FaUsers } from 'react-icons/fa';

const StudentDashboard = () => {
    const [studentName, setStudentName] = useState('');
    const [studentId, setStudentId] = useState('');
    const [currentCourses, setCurrentCourses] = useState([]);
    const [upcomingEvents, setUpcomingEvents] = useState([]);
    const [academicProgress, setAcademicProgress] = useState(0);
    const [notifications, setNotifications] = useState([]);
    const [enrolledClubs, setEnrolledClubs] = useState([]);

    useEffect(() => {
        const storedStudentName = localStorage.getItem('studentName');
        const storedStudentId = localStorage.getItem('studentId');

        if (storedStudentName && storedStudentId) {
            setStudentName(storedStudentName ? storedStudentName.split(' ')[0] : 'Student');
            setStudentId(storedStudentId);
            fetchStudentDashboardData(storedStudentId);
        }
    }, []);

    const fetchStudentDashboardData = async (studentId) => {
        // Replace with your actual API calls
        // Example:
        // try {
        //     const response = await fetch(`http://localhost:3001/api/students/${studentId}/dashboard`);
        //     if (response.ok) {
        //         const data = await response.json();
        //         setCurrentCourses(data.currentCourses);
        //         setUpcomingEvents(data.upcomingEvents);
        //         setAcademicProgress(data.academicProgress);
        //         setNotifications(data.notifications);
        //         setEnrolledClubs(data.enrolledClubs);
        //     }
        // } catch (error) {
        //     console.error("Error fetching student dashboard data:", error);
        // }

        // Dummy data for demonstration
        setCurrentCourses([
            { id: 1, name: 'Introduction to Programming', progress: 60 },
            { id: 2, name: 'Calculus I', progress: 45 },
        ]);
        setUpcomingEvents([
            { id: 1, title: 'Midterm Exam - Programming', date: 'May 8, 2025' },
            { id: 2, title: 'Guest Lecture on AI Ethics', date: 'May 12, 2025' },
        ]);
        setAcademicProgress(78);
        setNotifications([
            { id: 1, message: 'New assignment posted for Programming.', time: '2 hours ago' },
            { id: 2, message: 'Reminder: Calculus quiz tomorrow.', time: '1 day ago' },
        ]);
        setEnrolledClubs(['Coding Club', 'Debate Society']);
    };

    return (
        <main className={styles.mainContent}>
            <div className={styles.dashboardContainerInner}>
                <header className={styles.header}>
                    <div className={styles.headerWelcome}>
                        Welcome Back, <span className={styles.headerUserName}>{studentName}</span>!
                    </div>
                    <div className={styles.headerActions}>
                        <div className={styles.notificationBell}>
                            <FaBell className={styles.bellIcon} />
                            {notifications.length > 0 && <span className={styles.notificationBadge}>{notifications.length}</span>}
                        </div>
                        <div className={styles.userAvatar}>
                            <FaUserCircle className={styles.avatarIcon} />
                        </div>
                    </div>
                </header>

                <div className={styles.dashboardGrid}>
                    {/* Current Courses */}
                    <div className={styles.coursesCard}>
                        <h2 className={styles.cardTitle}><FaBookOpen /> Your Current Courses</h2>
                        {currentCourses.length > 0 ? (
                            <ul className={styles.courseList}>
                                {currentCourses.map(course => (
                                    <li key={course.id} className={styles.courseItem}>
                                        <span className={styles.courseName}>{course.name}</span>
                                        <div className={styles.progressBar}>
                                            <div className={styles.progress} style={{ width: `${course.progress}%` }}>{course.progress}%</div>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p className={styles.noData}>No current courses enrolled.</p>
                        )}
                        <button className={styles.viewMoreButton}>View All Courses</button>
                    </div>

                    {/* Upcoming Events */}
                    <div className={styles.eventsCard}>
                        <h2 className={styles.cardTitle}><FaCalendarAlt /> Upcoming Events</h2>
                        {upcomingEvents.length > 0 ? (
                            <ul className={styles.eventList}>
                                {upcomingEvents.map(event => (
                                    <li key={event.id} className={styles.eventItem}>
                                        <span className={styles.eventName}>{event.title}</span>
                                        <span className={styles.eventDate}>{event.date}</span>
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p className={styles.noData}>No upcoming events.</p>
                        )}
                        <button className={styles.viewMoreButton}>View Calendar</button>
                    </div>

                    {/* Academic Progress */}
                    <div className={styles.progressCard}>
                        <h2 className={styles.cardTitle}><FaChartLine /> Academic Progress</h2>
                        <div className={styles.progressCircle}>
                            <div className={styles.progressRing} style={{ '--progress': academicProgress }}>
                                <span>{academicProgress}%</span>
                            </div>
                        </div>
                        <p className={styles.progressText}>Overall Progress</p>
                    </div>

                    {/* Quick Tips/Announcements */}
                    <div className={styles.tipsCard}>
                        <h2 className={styles.cardTitle}><FaLightbulb /> Quick Tips</h2>
                        <ul className={styles.tipList}>
                            <li>Stay organized with a study schedule.</li>
                            <li>Don't hesitate to ask professors for help.</li>
                            <li>Join study groups for better understanding.</li>
                        </ul>
                        {notifications.length > 0 && (
                            <div className={styles.latestNotification}>
                                <FaBell className={styles.notificationIcon} />
                                {notifications[0].message}
                            </div>
                        )}
                    </div>

                    {/* Enrolled Clubs */}
                    <div className={styles.clubsCard}>
                        <h2 className={styles.cardTitle}><FaUsers /> Your Clubs</h2>
                        {enrolledClubs.length > 0 ? (
                            <div className={styles.clubList}>
                                {enrolledClubs.map(club => (
                                    <span key={club} className={styles.clubBadge}>{club}</span>
                                ))}
                            </div>
                        ) : (
                            <p className={styles.noData}>Not enrolled in any clubs yet.</p>
                        )}
                        <button className={styles.viewMoreButton}>Explore Clubs</button>
                    </div>

                    {/* You can add more personalized elements here */}
                </div>
            </div>
        </main>
    );
};

export default StudentDashboard;
