import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const EvaluateApplicants = () => {
  const { semester } = useParams();
  const [club, setClub] = useState(null);
  const [applicants, setApplicants] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    axios.get('http://localhost:3001/api/club/dashboard').then(res => {
      setClub(res.data.clubDetails);
      axios.get(`http://localhost:3001/api/recruitment/applicants/${res.data.clubDetails._id}/${semester}`)
        .then(res => setApplicants(res.data.applicants));
    });
  }, [semester]);

  const handleUpdate = (uid, newStatus) => {
    axios.post('http://localhost:3001/api/recruitment/evaluate', {
      clubId: club._id,
      semester,
      uid,
      action: newStatus
    }).then(() => {
      setApplicants(prev => prev.map(a => a.uid === uid ? { ...a, status: newStatus } : a));
    });
  };

  const counts = {
    pending: applicants.filter(a => a.status === 'pending').length,
    approved: applicants.filter(a => a.status === 'approved').length,
    rejected: applicants.filter(a => a.status === 'rejected').length,
  };

  const filteredApplicants = applicants.filter(a =>
    a.uname.toLowerCase().includes(searchTerm.toLowerCase()) ||
    a.uid.toString().includes(searchTerm)
  );

  return (
    <div style={{ padding: 20 }}>
      <h2>Evaluate Applicants ({semester})</h2>
      <h4>Club: {club?.cname}</h4>
      <p>Pending: {counts.pending} | Approved: {counts.approved} | Rejected: {counts.rejected}</p>

      <input
        type="text"
        placeholder="Search by name or ID"
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        style={{ marginBottom: 10 }}
      />

      {filteredApplicants.map(app => (
        <div key={app.uid} style={{ border: '1px solid #ccc', margin: '10px 0', padding: 10 }}>
          <p><b>{app.uname}</b> (ID: {app.uid})</p>
          <select
            value={app.status}
            onChange={e => handleUpdate(app.uid, e.target.value)}
          >
            <option value="pending">Pending</option>
            <option value="approved">Approved</option>
            <option value="rejected">Rejected</option>
          </select>
        </div>
      ))}
    </div>
  );
};

export default EvaluateApplicants;
