import React from 'react';
import { NavLink } from 'react-router-dom';
import styles from './ClubSidebar.module.css'; // Create this CSS module
import { FaCube, FaHome, FaCalendarAlt, FaUserPlus, FaUsers, FaUser, FaCog, FaSyncAlt } from 'react-icons/fa'; // Import necessary icons

const ClubSidebar = () => {
    const menuItems = [
        { path: '/club-dashboard/overview', label: 'Overview', icon: <FaHome className={styles.sidebarNavItemIcon} /> },
        { path: '/club-dashboard/events', label: 'Events', icon: <FaCalendarAlt className={styles.sidebarNavItemIcon} /> },
        { path: '/club-dashboard/recruitments', label: 'Recruitments', icon: <FaUserPlus className={styles.sidebarNavItemIcon} /> },
        { path: '/club-dashboard/members', label: 'Members', icon: <FaUsers className={styles.sidebarNavItemIcon} /> },
        { path: '/club-dashboard/profile', label: 'Profile', icon: <FaUser className={styles.sidebarNavItemIcon} /> },
        { path: '/club-dashboard/settings', label: 'Settings', icon: <FaCog className={styles.sidebarNavItemIcon} /> },
    ];

    return (
        <aside className={styles.sidebar}>
            <div className={styles.sidebarLogo}>
                <FaSyncAlt className={styles.sidebarLogoIcon} />
                <span className={styles.sidebarLogoText}>ClubSync</span>
            </div>
            <div className={styles.sidebarNav}>
                <div className={styles.adminTools}>Admin Tools</div>
                <nav>
                    {menuItems.map(item => (
                        <NavLink
                            key={item.path}
                            to={item.path}
                            className={({ isActive }) =>
                                `${styles.sidebarNavItem} ${isActive ? styles.sidebarNavItemActive : ''}`
                            }
                        >
                            {item.icon}
                            {item.label}
                        </NavLink>
                    ))}
                </nav>
            </div>
            <div className={styles.sidebarSupport}>
                <p className={styles.sidebarSupportText}>Have any problems with managing your dashboard? Contact our support.</p>
                <button className={styles.sidebarSupportButton}>Contact Us</button>
            </div>
        </aside>
    );
};

export default ClubSidebar;
