import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const ClubCard = ({ club }) => (
<div className="club-card">
{club.clogo && <img src={club.clogo} alt={`${club.cname} Logo`} className="club-logo" />}
<h3>{club.cname}</h3>
<p>{club.cdescription}</p>
<button>View Profile</button> {/* Link to detailed profile later */}
</div>
);

const AllClubs = () => {
const [clubs, setClubs] = useState([]);
const [loading, setLoading] = useState(true);
const [error, setError] = useState(null);

useEffect(() => {
const fetchClubs = async () => {
    try {
    const response = await fetch('/api/clubs'); // Replace with your actual API endpoint
    if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    setClubs(data);
    } catch (err) {
    setError(err.message);
    } finally {
    setLoading(false);
    }
};

fetchClubs();
}, []);

if (loading) {
return <div>Loading clubs...</div>;
}

if (error) {
return <div>Error loading clubs: {error}</div>;
}

return (
<div className="all-clubs-container">
    <h2>All University Clubs</h2>
    <div className="clubs-grid">
    {clubs.map(club => (
        <ClubCard key={club._id} club={club} /> 
    ))}
    </div>
</div>
);
};

const MyClubs = () => {
const [myClubs, setMyClubs] = useState([]);
const [loading, setLoading] = useState(true);
const [error, setError] = useState(null);
// Assuming you have a way to get the current user's ID
const userId = 123; // Replace with actual user ID

useEffect(() => {
const fetchMyClubs = async () => {
    try {
    const response = await fetch(`/api/users/${userId}/clubs`); // Replace with your actual API endpoint to get user's clubs
    if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    setMyClubs(data);
    } catch (err) {
    setError(err.message);
    } finally {
    setLoading(false);
    }
};

fetchMyClubs();
}, [userId]);

if (loading) {
return <div>Loading your clubs...</div>;
}

if (error) {
return <div>Error loading your clubs: {error}</div>;
}

return (
<div className="my-clubs-container">
    <h2>My Clubs</h2>
    {myClubs.length > 0 ? (
    <div className="clubs-grid">
        {myClubs.map(club => (
        <ClubCard key={club._id} club={club} />
        ))}
    </div>
    ) : (
    <p>You haven't joined any clubs yet.</p>
    )}
</div>
);
};

const JoinClubs = () => {
const [availableClubs, setAvailableClubs] = useState([]);
const [loading, setLoading] = useState(true);
const [error, setError] = useState(null);
const userId = 123; // Replace with actual user ID

useEffect(() => {
const fetchAvailableClubs = async () => {
    try {
    const response = await fetch(`/api/clubs/available/${userId}`); // Replace with your actual API endpoint to get clubs the user hasn't joined
    if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    setAvailableClubs(data);
    } catch (err) {
    setError(err.message);
    } finally {
    setLoading(false);
    }
};

fetchAvailableClubs();
}, [userId]);

const handleJoinClub = async (clubId) => {
// Assuming you have an API endpoint to handle joining a club
try {
    const response = await fetch(`/api/clubs/${clubId}/join`, {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json',
    },
    body: JSON.stringify({ userId }),
    });

    if (!response.ok) {
    const errorData = await response.json();
    console.error('Failed to join club:', errorData);
    alert(`Failed to join club: ${errorData.message || 'An error occurred'}`);
    return;
    }

    console.log(`Successfully joined club: ${clubId}`);
    // Optionally, update the list of available clubs or refetch user's clubs
    setAvailableClubs(availableClubs.filter(club => club._id !== clubId));
    // You might also want to update the 'My Clubs' view
} catch (err) {
    console.error('Error joining club:', err);
    alert('Error joining club. Please try again.');
}
};

if (loading) {
return <div>Loading available clubs...</div>;
}

if (error) {
return <div>Error loading available clubs: {error}</div>;
}

return (
<div className="join-clubs-container">
    <h2>Join New Clubs</h2>
    {availableClubs.length > 0 ? (
    <div className="clubs-grid">
        {availableClubs.map(club => (
        <div key={club._id} className="club-card">
            {club.clogo && <img src={club.clogo} alt={`${club.cname} Logo`} className="club-logo" />}
            <h3>{club.cname}</h3>
            <p>{club.cdescription}</p>
            <button onClick={() => handleJoinClub(club._id)}>Join Club</button>
        </div>
        ))}
    </div>
    ) : (
    <p>No new clubs available to join at the moment.</p>
    )}
</div>
);
};

const StudentClubsPage = () => {
return (
<div className="student-clubs-page">
    <nav>
    <ul>
        <li>
        <Link to="/">All Clubs</Link>
        </li>
        <li>
        <Link to="/my-clubs">My Clubs</Link>
        </li>
        <li>
        <Link to="/join-clubs">Join Clubs</Link>
        </li>
    </ul>
    </nav>

    <Routes>
    <Route path="/" element={<AllClubs />} />
    <Route path="/my-clubs" element={<MyClubs />} />
    <Route path="/join-clubs" element={<JoinClubs />} />
    </Routes>
</div>
);
};

export default StudentClubsPage;
