import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { FaTrash, FaFileInvoiceDollar } from 'react-icons/fa'; // Icons for delete and budget
import './PendingEvents.css'; // Import the CSS file

const PendingEvents = () => {
    const navigate = useNavigate();
    const [pendingEvents, setPendingEvents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPendingEvents = async () => {
            setLoading(true);
            setError(null);
            try {
                // Assuming you have the club name in local storage after login
                const clubName = localStorage.getItem('cName');
                if (!clubName) {
                    setError('Club name not found. Please log in again.');
                    setLoading(false);
                    return;
                }

                const response = await axios.get(
                    `http://localhost:3001/api/events/club/${clubName}`, // Fetch events for the logged-in club
                    { withCredentials: true }
                );

                const pending = response.data.filter(event => event.status === 'pending');

                // Sort pending events by event date
                const sortedEvents = [...pending].sort((a, b) => new Date(a.event_date) - new Date(b.event_date));

                setPendingEvents(sortedEvents);
                setLoading(false);

            } catch (err) {
                console.error('Error fetching pending events:', err);
                setError('Failed to load pending events.');
                setLoading(false);
            }
        };

        fetchPendingEvents();
    }, [navigate]);

    const handleDeleteEvent = async (eventId) => {
        if (window.confirm('Are you sure you want to delete this event?')) {
            try {
                await axios.delete(`http://localhost:3001/api/events/${eventId}`, { withCredentials: true });
                setPendingEvents(pendingEvents.filter(event => event.booking_id !== eventId)); // Update UI
                alert('Event deleted successfully!');
            } catch (err) {
                console.error('Error deleting event:', err);
                alert('Failed to delete event.');
            }
        }
    };

    const handleBudgetClick = (eventId) => {
        navigate(`/club/events/budget/${eventId}`); // Navigate to the budget page with event ID
    };

    if (loading) {
        return <div className="pending-events-container loading">Loading pending events...</div>;
    }

    if (error) {
        return <div className="pending-events-container error">Error: {error}</div>;
    }

    return (
        <div className="pending-events-container">
            <h2 className="pending-events-title">Pending Event Requests</h2>
            {pendingEvents.length === 0 ? (
                <p className="no-pending-events">No pending event requests at the moment.</p>
            ) : (
                <div className="pending-events-table-wrapper">
                    <table className="pending-events-table">
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Event Name</th>
                                <th>Event Date</th>
                                <th>Time Slots</th>
                                <th>Room No.</th>
                                <th>Student Reg.</th>
                                <th>Event Details</th>
                                <th>Status</th>
                                <th>Comments</th>
                                <th>Budget</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {pendingEvents.map((event, index) => (
                                <tr key={event.booking_id}>
                                    <td>{index + 1}</td>
                                    <td>{event.event_name}</td>
                                    <td>{new Date(event.event_date).toLocaleDateString()}</td>
                                    <td>{Array.isArray(event.time_slots) ? event.time_slots.join(', ') : event.time_slot}</td> {/* Handle both array and string formats */}
                                    <td>{event.room_number}</td>
                                    <td>{event.std_reg}</td>
                                    <td className="event-details-cell">{event.event_details}</td>
                                    <td className="status-pending">{event.status}</td>
                                    <td>{event.comments || '-'}</td>
                                    <td>
                                        <button
                                            className="budget-button"
                                            onClick={() => handleBudgetClick(event.booking_id)}
                                        >
                                            <FaFileInvoiceDollar /> Budget
                                        </button>
                                    </td>
                                    <td>
                                        <button
                                            className="delete-button"
                                            onClick={() => handleDeleteEvent(event.booking_id)}
                                        >
                                            <FaTrash /> Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default PendingEvents;
