// src/students/JoinClubsPage.jsx
import React, { useState, useEffect } from 'react';
import './JoinClubsPage.css'; // Import the CSS file from the same directory

const API_BASE_URL = 'http://localhost:3001/api';

// Helper to handle fetch responses (No change needed here)
async function handleResponse(response) {
    const data = await response.json();
    if (!response.ok) {
        const error = new Error(data.message || 'Something went wrong');
        error.status = response.status;
        error.data = data;
        throw error;
    }
    return data;
}

// Check if student is authenticated
const checkStudentAuth = async () => {
    const response = await fetch(`${API_BASE_URL}/students/check-auth`, {
        credentials: 'include' // <--- ADD THIS
    });
    const data = await response.json();
    data.status = response.status;
    return data;
};

// Fetch all currently recruiting clubs (Might or might not need auth, but safer to include if showing personalized status)
const fetchRecruitingClubs = async () => {
    const response = await fetch(`${API_BASE_URL}/recruitment/recruiting`, {
        credentials: 'include' // <--- ADD THIS if the backend recruitment endpoint requires auth
                               // or if it uses session to get student ID for status check
    });
    return handleResponse(response);
};

// Register student for a club recruitment
const registerForClub = async (recruitmentId) => {
    const response = await fetch(`${API_BASE_URL}/students/register-club`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ recruitmentId }),
        credentials: 'include' // <--- ADD THIS
    });
    return handleResponse(response);
};

// Fetch pending interviews count for the logged-in student
const fetchPendingInterviewsCount = async () => {
    const response = await fetch(`${API_BASE_URL}/students/pending-interviews`, {
        credentials: 'include' // <--- ADD THIS
    });
    return handleResponse(response);
};
// --- End API Call Functions ---


function JoinClubsPage() {
    const [recruitingClubs, setRecruitingClubs] = useState([]);
    const [filteredClubs, setFilteredClubs] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [pendingCount, setPendingCount] = useState(0);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [student, setStudent] = useState(null); // To store logged-in student info

    // --- Authentication Check and Initial Data Fetch ---
    useEffect(() => {
        const initializePage = async () => {
            try {
                // Check Authentication
                const authData = await checkStudentAuth();
                 if (!authData.isLoggedIn) {
                    console.log("Student not logged in, redirecting...");
                    // Use window.location for a simple redirect, replace with React Router navigate if used
                    window.location.href = '/login'; // Adjust login page path
                    return; // Stop execution
                }
                setStudent(authData.student); // Store student info from session

                // Fetch initial data only if authenticated
                const clubsData = await fetchRecruitingClubs();
                setRecruitingClubs(clubsData);
                setFilteredClubs(clubsData); // Initially show all clubs

                const pendingData = await fetchPendingInterviewsCount();
                setPendingCount(pendingData.pendingCount);

            } catch (err) {
                console.error("Initialization Error:", err);
                 if (err.status !== 401) { // Avoid showing error if it's just an auth redirect
                     setError("Failed to load data. Please try again.");
                 }
            } finally {
                setLoading(false);
            }
        };

        initializePage();
    }, []); // Empty dependency array means this runs once on mount

    // --- Handle Search Logic ---
    useEffect(() => {
        const query = searchTerm.toLowerCase();
        const filtered = recruitingClubs.filter(club => {
            // Search by club name or numeric club ID (cid)
            const nameMatch = club.clubName && club.clubName.toLowerCase().includes(query);
            // Convert cid to string for searching, handle null/undefined cid
            const idMatch = club.cid != null && String(club.cid).includes(query);
            return nameMatch || idMatch;
        });
        setFilteredClubs(filtered);
    }, [searchTerm, recruitingClubs]); // Re-run filter when search term or clubs list changes


    // --- Handle Registration ---
    const handleRegister = async (recruitmentId) => {
        // Find the club in the current filtered list to potentially update its button state immediately
        // This is a basic approach; managing loading state per card is more complex.
        // We'll just rely on the state update after the fetch for simplicity as requested.

        setError(null); // Clear previous errors

        try {
            const result = await registerForClub(recruitmentId);
            console.log("Registration result:", result);

            // Update pending count from the backend response
            setPendingCount(result.pendingCount);

            // Update the local state for recruitingClubs to reflect the student's new pending status
            // This ensures the button changes to "Already Applied" without a full page reload.
            setRecruitingClubs(prevClubs =>
                 prevClubs.map(club => {
                     // Check if this is the club that was registered for
                     if (club._id === recruitmentId && student && student.uid) {
                         const studentUidNumber = Number(student.uid);
                         // If the student's UID is not already in pending, add it
                         if (!club.pending_std.includes(studentUidNumber)) {
                            return {
                                ...club,
                                pending_std: [...club.pending_std, studentUidNumber] // Add student UID to pending list
                            };
                         }
                          // If student was already pending, return the club as is
                          return club;
                     }
                     return club; // Return unchanged club if not the one being updated
                 })
            );

            // The useEffect for search will automatically re-filter filteredClubs based on the updated recruitingClubs state.


        } catch (err) {
            console.error("Registration Error:", err);
             // Display a user-friendly error message from the backend or a generic one
            setError(err.data?.message || err.message || "An unexpected error occurred during registration.");
            // The button state will implicitly revert if the fetch failed before the state update
            // but managing a specific 'isSubmitting' state per card would be more robust.
        }
    };


    // --- Render UI ---
    if (loading) {
        return (
             <div className="container">
                 <h1>Join Clubs</h1>
                 <p className="loading-message">Loading recruiting clubs...</p>
             </div>
        );
    }

    // Only show a global error message if there's an error AND not loading
    if (error && !loading) {
        return (
            <div className="container">
                 <h1>Join Clubs</h1>
                 <p className="error-message">{error}</p>
            </div>
        );
    }

    return (
        <div className="container">
            <h1>Join Clubs</h1>

            <div className="info-bar">
                <div className="pending-interviews">
                    Pending Applications: <span id="pending-count">{pendingCount}</span>
                </div>
                <div className="search-bar">
                    <input
                        type="text"
                        id="search-input"
                        placeholder="Search by Club Name or ID"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                     {/* Search button is optional if filtering happens on input change */}
                     {/* <button id="search-button" onClick={() => setSearchTerm(searchTerm)}>Search</button> */}
                </div>
            </div>

            {/* Although we have a global error state, you could still display errors here */}
            {/* {error && <div className="error-message">{error}</div>} */}


            <div id="clubs-grid" className="clubs-grid">
                 {/* Render Club Cards directly within the map */}
                {filteredClubs.length > 0 ? (
                     filteredClubs.map(club => {
                         const studentUidNumber = Number(student?.uid); // Get student UID safely

                         // Determine button/status state for *this* club card
                         const isApproved = student && club.approved_std.includes(studentUidNumber);
                         const isRejected = student && club.rejected_std.includes(studentUidNumber);
                         const isPending = student && club.pending_std.includes(studentUidNumber);

                         let buttonContent;
                         let buttonDisabled = false;
                         let statusMessage = null; // To display messages like Approved/Rejected

                         if (isApproved) {
                             statusMessage = 'Approved';
                             buttonDisabled = true;
                         } else if (isRejected) {
                             statusMessage = 'Rejected';
                             buttonDisabled = true;
                         } else if (isPending) {
                              buttonContent = 'Already Applied';
                              buttonDisabled = true;
                         } else {
                             buttonContent = 'Register';
                             buttonDisabled = false;
                         }

                         return (
                            <div key={club._id} className="club-card"> {/* Use recruitment ID as key */}
                                <h3>{club.clubName} ({club.cid || 'N/A'})</h3>
                                <p>Semester: {club.semester}</p>
                                <p>{club.description || 'No description provided.'}</p>
                                <div className="card-action-area"> {/* Flex container for button/message */}
                                    {statusMessage ? (
                                        <span className={`status-message status-${statusMessage.toLowerCase()}`}>{statusMessage}</span>
                                    ) : (
                                        <button
                                            className="register-button"
                                            onClick={() => handleRegister(club._id)} // Call handler with recruitment ID
                                            disabled={buttonDisabled}
                                        >
                                            {buttonContent}
                                        </button>
                                    )}
                                </div>
                            </div>
                         );
                     })
                ) : (
                     // Show message if no clubs found or no results after search
                    recruitingClubs.length > 0 && searchTerm !== '' ? (
                        <p className="no-clubs-message">No clubs match your search.</p>
                    ) : (
                        <p className="no-clubs-message">No clubs are currently recruiting.</p>
                    )
                )}
            </div>

        </div>
    );
}

export default JoinClubsPage;
