// frontend/src/students/UpcomingEvents.js
import React, { useState, useEffect, useContext /* or useSelector if using Redux */ } from 'react';
import axios from 'axios';
import styles from './UpcomingEvents.module.css';
import { FaCalendarAlt, FaClock, FaMapMarkerAlt, FaUniversity, FaInfoCircle, FaSearch, FaFilter, FaUserCheck, FaExclamationCircle } from 'react-icons/fa';
import { BeatLoader } from 'react-spinners';

function UpcomingEvents() {
const [events, setEvents] = useState([]);
const [filteredEvents, setFilteredEvents] = useState([]);
const [loading, setLoading] = useState(true);
const [error, setError] = useState(null);
// --- Added State for Search and Filter ---
const [searchTerm, setSearchTerm] = useState('');
const [selectedClub, setSelectedClub] = useState('');
const [allClubs, setAllClubs] = useState([]); // State to store unique club names for the filter
// --- End Added State ---
const [registrationStatus, setRegistrationStatus] = useState({});

// --- Get Logged-in Student Info ---
const getStudentIdentifierFromStorage = () => {
    try {
       // Looking for 'studentId' based on your Login.jsx
       const storedId = localStorage.getItem('studentId');

       if (storedId) {
           // Attempt to parse as number, but log if it fails as per previous discussion
           const idAsNumber = parseInt(storedId, 10);
           if (!isNaN(idAsNumber)) {
               console.log("Parsed numeric student ID from storage:", idAsNumber);
               return idAsNumber; // Return the numeric UID if it was stored/parsed
           } else {
               console.log("Stored student ID is not a number, returning null. Ensure your login component stores the numeric UID under 'studentUid'.", storedId);
               return null; // Return null if it's not the expected numeric UID
           }
       }
    } catch (e) {
        console.error("Error reading student info from localStorage", e);
    }
    return null;
};

const loggedInStudentIdentifier = getStudentIdentifierFromStorage();

// --- Check Authentication on Mount (Optional but Recommended) ---
useEffect(() => {
    if (!loggedInStudentIdentifier) {
            console.warn("UpcomingEvents: No logged-in student identifier found from storage. Registration might fail.");
    } else {
        console.log("UpcomingEvents: Logged-in student identifier found from storage:", loggedInStudentIdentifier);
    }
}, [loggedInStudentIdentifier]);

// --- Fetch Approved Events ---
    useEffect(() => {
        setLoading(true);
        setError(null);
        axios.get('http://localhost:3001/api/student/events/upcoming', { withCredentials: true })
            .then(response => {
                const sortedEvents = response.data.sort((a, b) => new Date(a.event_date) - new Date(b.event_date));
                setEvents(sortedEvents);
                // Initially, filteredEvents is the same as all events
                setFilteredEvents(sortedEvents);
                // Extract unique club names and sort them for the filter dropdown
                const clubs = [...new Set(sortedEvents.map(event => event.club_name))];
                setAllClubs(clubs.sort());
            })
            .catch(err => {
                console.error("Error fetching upcoming events:", err);
               if (err.response) {
                    console.error("Error data:", err.response.data);
                    console.error("Error status:", err.response.status);
                    if (err.response.status === 401) {
                        setError("You must be logged in to view upcoming events.");
                         console.log("Received 401, potentially session expired or invalid cookie.");
                    } else {
                        setError(err.response.data.message || `Error: ${err.response.status}`);
                    }
               } else if (err.request) {
                    console.error("Error request:", err.request);
                    setError("Could not connect to server. Is it running on port 3001?");
               } else {
                    console.error('Error message:', err.message);
                    setError(`An error occurred: ${err.message}`);
               }
            })
            .finally(() => {
                setLoading(false);
            });
}, []); // Fetch on mount

// --- Filter Events based on Search and Club Selection ---
    useEffect(() => {
        let currentEvents = [...events]; // Start with the full list of events

        // Apply search filter if searchTerm is not empty
        if (searchTerm.trim()) {
            currentEvents = currentEvents.filter(event =>
                // Search by event ID (eid) - case-insensitive
                event.eid.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        // Apply club filter if a club is selected
        if (selectedClub) {
            currentEvents = currentEvents.filter(event =>
                event.club_name === selectedClub
            );
        }

        // Update the filtered events state
        setFilteredEvents(currentEvents);
    }, [searchTerm, selectedClub, events]); // Re-run filter whenever search, filter, or events change


// --- Handle Event Registration ---
const handleRegister = (eventId, eventName) => {
    if (!loggedInStudentIdentifier) {
        alert('Could not identify student. Please ensure you are logged in.');
        return;
    }

    const confirmation = window.confirm(`Are you sure you want to register for "${eventName}"?`);
    if (confirmation) {
        setRegistrationStatus(prev => ({ ...prev, [eventId]: { loading: true } }));

        axios.post(`http://localhost:3001/api/student/events/register/${eventId}`,
            {},
            { withCredentials: true }
        )
        .then(response => {
            console.log('Registration successful/status:', response.data);
            const message = response.data.message || 'Registration successful!';
            const isAlreadyRegistered = response.data.alreadyRegistered || false;

            setRegistrationStatus(prev => ({ ...prev, [eventId]: { success: true, message: message } }));

            if (!isAlreadyRegistered) {
                    setEvents(prevEvents => prevEvents.map(event => {
                    if (event._id === eventId) {
                        const currentRegStd = Array.isArray(event.reg_std) ? event.reg_std : [];
                        // Use loggedInStudentIdentifier for updating state
                        const updatedRegStd = currentRegStd.includes(loggedInStudentIdentifier)
                            ? currentRegStd
                            : [...currentRegStd, loggedInStudentIdentifier];
                        return { ...event, reg_std: updatedRegStd };
                    }
                    return event;
                    }));
            }

                 setTimeout(() => {
                 setRegistrationStatus(prev => ({...prev, [eventId]: undefined}));
                 }, 5000);
        })
        .catch(err => {
            console.error("Error registering for event:", err);
            let errorMessage = 'Registration failed. Please try again.';
                 if (err.response?.status === 401) {
                     errorMessage = "Authentication failed during registration. Please log in again.";
                 } else {
                     errorMessage = err.response?.data?.message || errorMessage;
                 }
            setRegistrationStatus(prev => ({ ...prev, [eventId]: { error: true, message: errorMessage } }));
                 setTimeout(() => {
                 setRegistrationStatus(prev => ({...prev, [eventId]: undefined}));
                 }, 7000);
        });
    }
};

// --- Helper formatDateTime (remains the same) ---
    const formatDateTime = (dateString, timeSlots) => {
    const date = new Date(dateString);
    const formattedDate = date.toLocaleDateString('en-US', {
        year: 'numeric', month: 'long', day: 'numeric', weekday: 'long'
    });
    const formattedTime = timeSlots?.join(' / ') || 'Time TBD';
    return `${formattedDate} at ${formattedTime}`;
    };

// --- JSX Rendering ---
return (
    <div className={styles.upcomingEventsContainer}>
        <h1><FaCalendarAlt /> Upcoming Approved Events</h1>

        {/* --- Search and Filter Controls --- */}
            <div className={styles.controls}>
                <div className={styles.searchInputContainer}>
                    <FaSearch className={styles.searchIcon} />
                    <input
                        type="text"
                        placeholder="Search by Event ID..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className={styles.searchInput}
                    />
                </div>
                <div className={styles.filterDropdownContainer}>
                     <FaFilter className={styles.filterIcon} />
                    <select
                        value={selectedClub}
                        onChange={(e) => setSelectedClub(e.target.value)}
                        className={styles.filterDropdown}
                    >
                        <option value="">All Clubs</option> {/* Option to show all clubs */}
                        {allClubs.map(club => (
                            <option key={club} value={club}>{club}</option>
                        ))}
                    </select>
                </div>
            </div>
        {/* --- End Search and Filter Controls --- */}


            {/* Loading State */}
            {loading && <div className={styles.centeredMessage}><BeatLoader color="#4A90E2" loading={loading} size={15} /><p>Loading events...</p></div>}

            {/* Error State */}
            {error && !loading && <div className={`${styles.centeredMessage} ${styles.errorMessage}`}><FaExclamationCircle size={24} /><p>{error}</p></div>}

            {/* No Events Found State */}
            {!loading && !error && filteredEvents.length === 0 && (
                 <div className={styles.centeredMessage}>
                     <p>{events.length === 0 ? "No upcoming events found." : "No events match your current search/filter."}</p>
                 </div>
            )}


            {/* Events Grid */}
            {!loading && !error && filteredEvents.length > 0 && (
                <div className={styles.eventsGrid}>
                    {filteredEvents.map(event => {
                        const isRegistered = loggedInStudentIdentifier && Array.isArray(event.reg_std) && event.reg_std.includes(loggedInStudentIdentifier);
                        const regInfo = registrationStatus[event._id];

                        return (
                            <div key={event._id} className={styles.eventCard}>
                                <h3>{event.event_name}</h3>
                                <p className={styles.eventMeta}><FaUniversity /> <strong>Club:</strong> {event.club_name}</p>
                                <p className={styles.eventMeta}><FaCalendarAlt /> <strong>Date & Time:</strong> {formatDateTime(event.event_date, event.time_slots)}</p>
                                <p className={styles.eventMeta}><FaMapMarkerAlt /> <strong>Location:</strong> Room {event.room_number}</p>
                                <p className={styles.eventMeta}><FaInfoCircle /> <strong>EID:</strong> {event.eid}</p>
                                <p className={styles.eventDetails}>{event.event_details?.substring(0, 100)}{event.event_details?.length > 100 ? '...' : ''}</p>


                                {/* Registration Button Area */}
                                {event.std_reg === 'Yes' && (
                                    <div className={styles.registrationArea}>
                                        <button
                                            className={`${styles.registerButton} ${isRegistered ? styles.registered : ''} ${regInfo?.loading ? styles.loading : ''}`}
                                            onClick={() => !isRegistered && !regInfo?.loading && loggedInStudentIdentifier && handleRegister(event._id, event.event_name)}
                                            disabled={isRegistered || regInfo?.loading || !loggedInStudentIdentifier}
                                            title={!loggedInStudentIdentifier ? "Please log in to register" : (isRegistered ? "You are already registered" : `Register for ${event.event_name}`)}
                                        >
                                            {regInfo?.loading ? <BeatLoader size={8} color="#fff" /> : (isRegistered ? <> <FaUserCheck /> Registered </> : 'Register Now')}
                                        </button>
                                        {regInfo?.success && <span className={styles.regSuccess}>{regInfo.message}</span>}
                                        {regInfo?.error && <span className={styles.regError}>{regInfo.message}</span>}
                                        {!loggedInStudentIdentifier && <span className={styles.regMessage}>Log in to register.</span>}
                                    </div>
                                )}
                                {event.std_reg !== 'Yes' && (
                                    <p className={styles.noRegistration}>Registration not available</p>
                                )}
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
);
}

export default UpcomingEvents;
