import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { cn } from "@/lib/utils"

const ClubRecruitments = () => {
const { clubId } = useParams();
const [club, setClub] = useState(null);
const [semesterData, setSemesterData] = useState([]);
const [loading, setLoading] = useState(true);
const [error, setError] = useState('');
const [selectedSemesters, setSelectedSemesters] = useState([]);
const [recruitmentStatus, setRecruitmentStatus] = useState('');
const navigate = useNavigate();


useEffect(() => {
const fetchClubData = async () => {
    try {
    setLoading(true);
    const response = await axios.get(`http://localhost:3001/api/clubs/details/${clubId}`);
    setClub(response.data.club);
    setSemesterData(response.data.semesterData);
    setRecruitmentStatus(response.data.club.creq)
    } catch (err) {
    setError(err.message || 'Failed to fetch club data.');
    } finally {
    setLoading(false);
    }
};

fetchClubData();
}, [clubId]);

const handleSemesterChange = (value) => {
    setSelectedSemesters(value);
};

const handleStartRecruitment = async () => {
try {
    if (!selectedSemesters || selectedSemesters.length === 0) {
    setError('Please select at least one semester.');
    return;
    }
    setError('');
    const response = await axios.post(`http://localhost:3001/api/clubs/set-recruitment/${clubId}`, {
    semester: selectedSemesters,
    recruitmentStatus: 'Yes'
    });
    setClub(response.data.club);
        setRecruitmentStatus(response.data.club.creq);
    setSemesterData(prevData => {
            // If semester data already exists, update it.  If not, create it.
            const newSemesterData = selectedSemesters.map(sem => {
                const existing = prevData.find(item => item.semester === sem);
                return existing ? existing : {
                    semester: sem,
                    totalApplicants: 0,
                    pending: 0,
                    accepted: 0,
                    rejected: 0
                };
            });
            return newSemesterData;
        });

} catch (err) {
    setError(err.response?.data?.message || 'Failed to start recruitment.');
}
};

const handleEndRecruitment = async() => {
        try {

    const response = await axios.post(`http://localhost:3001/api/clubs/set-recruitment/${clubId}`, {
    semester: [], //clear semester
        recruitmentStatus: 'No'
    });
    setClub(response.data.club);
        setRecruitmentStatus(response.data.club.creq);
    setSemesterData([]);

} catch (err) {
    setError(err.response?.data?.message || 'Failed to end recruitment.');
}
}

const handleEvaluation = (semester) => {
    navigate(`/club/${clubId}/evaluation/${semester}`);
}

if (loading) {
return <div>Loading...</div>; // Replace with a proper loading indicator
}

if (error) {
return <div className="text-red-500">{error}</div>;
}

if (!club) {
return <div>Club not found.</div>;
}

return (
<div className="p-4">
    <h2 className="text-2xl font-bold mb-4">Club Recruitment: {club.cname}</h2>
    <p>Club ID: {club.cid}</p>
    <p>Recruitment Status: {recruitmentStatus}</p>

    {recruitmentStatus === 'No' ? (
    <div className="mt-4">
        <Select onValueChange={handleSemesterChange} value={selectedSemesters}>
        <SelectTrigger className="w-full">
            <SelectValue placeholder="Select Semesters" />
        </SelectTrigger>
        <SelectContent>
            {['1st', '2nd', '3rd', '4th', '5th', '6th', '7th', '8th'].map((semester) => (
            <SelectItem key={semester} value={semester}>
                {semester}
            </SelectItem>
            ))}
        </SelectContent>
        </Select>
        <Button onClick={handleStartRecruitment} className="mt-2">
        Start Recruitment
        </Button>
    </div>
    ) : (
    <div className="mt-4">
        <Button onClick={handleEndRecruitment}>End Recruitment</Button>
    </div>
    )}

    <h3 className="text-xl font-semibold mt-6">Recruiting Semesters</h3>
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
    {semesterData.map((semesterInfo) => (
        <Card key={semesterInfo.semester} className="border-2 shadow-lg">
        <CardHeader>
            <CardTitle>{semesterInfo.semester}</CardTitle>
            <CardDescription>Recruitment Details</CardDescription>
        </CardHeader>
        <CardContent>
            <div className="space-y-2">
            <p>Total Applicants: <Badge variant="secondary">{semesterInfo.totalApplicants}</Badge></p>
            <p>Pending: <Badge variant="outline">{semesterInfo.pending}</Badge></p>
            <p>Accepted: <Badge className="bg-green-500 text-white">{semesterInfo.accepted}</Badge></p>
            <p>Rejected: <Badge className="bg-red-500 text-white">{semesterInfo.rejected}</Badge></p>
            <Button onClick={() => handleEvaluation(semesterInfo.semester)} className="mt-2 w-full">
                Evaluate
            </Button>
            </div>
        </CardContent>
        </Card>
    ))}
    </div>
</div>
);
};

export default ClubRecruitments;
