import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './ClubDashboard.module.css';
import ClubSidebar from '../pages/ClubSidebar'; // Import the new component
import { FaUsers, FaCalendarCheck, FaBullhorn, FaMoneyBillAlt, FaUserCircle, FaChevronRight, FaUpload, FaSearch, FaBell } from 'react-icons/fa';

const ClubDashboard = () => {
    const navigate = useNavigate();
    const [clubInfo, setClubInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDashboardData = async () => {
            setLoading(true);
            setError(null);
            try {
                const response = await fetch('http://localhost:3001/api/clubs/dashboard', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    credentials: 'include'
                });

                if (!response.ok) {
                    if (response.status === 401) {
                        console.log('Unauthorized access to dashboard, redirecting to login.');
                        navigate('/login-club');
                        return;
                    }
                    const errorData = await response.json();
                    throw new Error(errorData?.message || `HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                setClubInfo(data);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching dashboard data:', err);
                setError(err.message || 'Failed to load dashboard data.');
                setLoading(false);
            }
        };

        fetchDashboardData();
    }, [navigate]);

    

    if (loading) {
      return <div className={styles.loading}>Loading dashboard...</div>;
  }

  if (error) {
      return <div className={styles.error}>Error loading dashboard: {error}</div>;
  }

  return (
    <div className={styles.dashboardContainer}>
            <ClubSidebar />
            <main className={styles.mainContent}>
                <header className={styles.header}>
                    <div className={styles.headerWelcome}>
                        Welcome Back, <span className={styles.headerUserName}>{clubInfo?.clubName || 'Lucy Lure'}</span>
                    </div>
                    <div className={styles.headerActions}>
                        <button className={styles.uploadButton}>
                            <FaUpload /> Upload Product
                        </button>
                        <div className={styles.searchBar}>
                            <FaSearch className={styles.searchIcon} />
                            <input type="text" placeholder="Search" className={styles.searchInput} />
                        </div>
                        <div className={styles.notificationIcon}>
                            <FaBell />
                            <span className={styles.notificationBadge}>2</span>
                        </div>
                        {/* REMOVE THE LOGOUT BUTTON HERE */}
                    </div>
                </header>

        <div className={styles.dashboardGrid}>
                  <div className={`${styles.dashboardCard}`}>
                      <div className={styles.cardHeader}>
                          <FaUsers className={styles.cardHeaderIcon} />
                          <span className={styles.cardTitle}>Total Members</span>
                      </div>
                      <div className={styles.cardValue}>150</div>
                      <div className={styles.cardTrend}>+5 new members this week</div>
                  </div>

    <div className={`${styles.dashboardCard}`}>
        <div className={styles.cardHeader}>
            <FaCalendarCheck className={styles.cardHeaderIcon} />
            <span className={styles.cardTitle}>Upcoming Events</span>
        </div>
        <div className={styles.cardValue}>3</div>
        <div className={styles.cardTrend}>View schedule</div>
    </div>

    <div className={`${styles.dashboardCard}`}>
        <div className={styles.cardHeader}>
            <FaBullhorn className={styles.cardHeaderIcon} />
            <span className={styles.cardTitle}>Active Announcements</span>
        </div>
        <div className={styles.cardValue}>2</div>
        <div className={styles.cardTrend}>See details</div>
    </div>

    <div className={`${styles.dashboardCard}`}>
        <div className={styles.cardHeader}>
            <FaMoneyBillAlt className={styles.cardHeaderIcon} />
            <span className={styles.cardTitle}>Pending Dues</span>
        </div>
        <div className={styles.cardValue}>$250</div>
        <div className={styles.cardTrend}>Manage payments</div>
    </div>

    {/* You can keep or modify the Top Products and Balances sections */}
    <div className={`${styles.dashboardCard} ${styles.topProductsCard}`}>
        <h2>Top Active Members</h2>
        {/* Replace with actual top members data when available */}
        {[1, 2, 3].map(i => (
            <div key={i} className={styles.topProductItem}>
                <div className={styles.productAvatar}><FaUserCircle /></div> {/* Placeholder avatar */}
                <div className={styles.productInfo}>
                    <div className={styles.productName}>Member {i}</div>
                    <div className={styles.productCategory}>Joined 2 months ago</div>
                </div>
                <FaChevronRight className={styles.viewDetailsArrow} />
            </div>
        ))}
    </div>

    <div className={`${styles.dashboardCard} ${styles.balancesCard}`}>
        <h2>Club Funds</h2>
        <div className={styles.balanceItem}>
            <div className={styles.balanceLabel}>Current Balance</div>
            <div className={styles.balanceValue}>$1200</div>
        </div>
        <button className={styles.withdrawButton}>Manage Funds</button>
    </div>
</div>
            </main>
        </div>
    );
};

export default ClubDashboard;
